// frontend/src/components/public/FormularioPostulacion.jsx
import React, { useState, useEffect } from 'react';
import postulacionesPublicasService from '../../api/postulacionesPublicasService';
import {
  Briefcase, User, Mail, Phone, MapPin, Calendar, FileText,
  Upload, CheckCircle, AlertCircle, ArrowRight, ArrowLeft,
  Building, Award, Languages, Code, Car, X, Loader
} from 'lucide-react';

const FormularioPostulacion = () => {
  const [paso, setPaso] = useState(1);
  const [vacantes, setVacantes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [enviando, setEnviando] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const [datos, setDatos] = useState({
    vacanteId: '',
    nombre: '',
    email: '',
    cedula: '',
    telefono: '',
    telefonoMovil: '',
    telefonoFijo: '',
    fechaNacimiento: '',
    genero: '',
    estadoCivil: '',
    nacionalidad: 'Dominicana',
    direccion: '',
    ciudad: '',
    provincia: '',
    nivelEducativo: '',
    institucion: '',
    tituloObtenido: '',
    anioGraduacion: '',
    experienciaLaboral: '',
    experienciaAnios: '',
    ultimoEmpleador: '',
    ultimoPuesto: '',
    expectativaSalarial: '',
    motivacion: '',
    conocimientosEspeciales: '',
    idiomas: '',
    softwareConocido: '',
    referenciaPersonal1: '',
    referenciaPersonal2: '',
    disponibilidadInmediata: true,
    disponibilidadViajar: false,
    tieneLicencia: false,
    tipoLicencia: '',
    comoConocioVacante: ''
  });

  const [archivos, setArchivos] = useState({
    cv: null,
    fotoCedula: null,
    foto2x2: null
  });

  useEffect(() => {
    cargarVacantes();
  }, []);

  const cargarVacantes = async () => {
    try {
      setLoading(true);
      const data = await postulacionesPublicasService.getVacantesPublicas();
      setVacantes(data);
    } catch (err) {
      setError('Error al cargar las vacantes disponibles');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setDatos(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (files && files[0]) {
      setArchivos(prev => ({
        ...prev,
        [name]: files[0]
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setEnviando(true);
      setError(null);
      
      await postulacionesPublicasService.enviarPostulacion(datos, archivos);
      
      setSuccess(true);
      window.scrollTo(0, 0);
    } catch (err) {
      setError(err.response?.data?.error || 'Error al enviar la postulación');
      window.scrollTo(0, 0);
    } finally {
      setEnviando(false);
    }
  };

  const siguientePaso = () => {
    if (validarPaso(paso)) {
      setPaso(paso + 1);
      window.scrollTo(0, 0);
    }
  };

  const pasoAnterior = () => {
    setPaso(paso - 1);
    window.scrollTo(0, 0);
  };

  const validarPaso = (pasoActual) => {
    switch (pasoActual) {
      case 1:
        if (!datos.vacanteId) {
          setError('Debe seleccionar una vacante');
          return false;
        }
        if (!datos.nombre || !datos.email || !datos.cedula) {
          setError('Complete todos los campos requeridos');
          return false;
        }
        break;
      case 2:
        if (!datos.nivelEducativo) {
          setError('Indique su nivel educativo');
          return false;
        }
        break;
      case 3:
        if (!datos.experienciaLaboral) {
          setError('Describa su experiencia laboral');
          return false;
        }
        break;
    }
    setError(null);
    return true;
  };

  if (loading) {
    return (
      <div style={styles.container}>
        <div style={styles.loading}>
          <Loader size={48} style={{ color: '#7c3aed', animation: 'spin 1s linear infinite' }} />
          <p>Cargando formulario...</p>
        </div>
      </div>
    );
  }

  if (success) {
    return (
      <div style={styles.container}>
        <div style={styles.successCard}>
          <CheckCircle size={64} style={{ color: '#10b981', marginBottom: '1rem' }} />
          <h2 style={styles.successTitle}>¡Postulación Enviada Exitosamente!</h2>
          <p style={styles.successText}>
            Gracias por postularte. Hemos recibido tu información y la revisaremos pronto.
            Nos pondremos en contacto contigo si tu perfil coincide con nuestras necesidades.
          </p>
          <button
            onClick={() => {
              setSuccess(false);
              setPaso(1);
              setDatos({
                vacanteId: '',
                nombre: '',
                email: '',
                cedula: '',
                telefono: '',
                telefonoMovil: '',
                telefonoFijo: '',
                fechaNacimiento: '',
                genero: '',
                estadoCivil: '',
                nacionalidad: 'Dominicana',
                direccion: '',
                ciudad: '',
                provincia: '',
                nivelEducativo: '',
                institucion: '',
                tituloObtenido: '',
                anioGraduacion: '',
                experienciaLaboral: '',
                experienciaAnios: '',
                ultimoEmpleador: '',
                ultimoPuesto: '',
                expectativaSalarial: '',
                motivacion: '',
                conocimientosEspeciales: '',
                idiomas: '',
                softwareConocido: '',
                referenciaPersonal1: '',
                referenciaPersonal2: '',
                disponibilidadInmediata: true,
                disponibilidadViajar: false,
                tieneLicencia: false,
                tipoLicencia: '',
                comoConocioVacante: ''
              });
              setArchivos({ cv: null, fotoCedula: null, foto2x2: null });
            }}
            style={styles.button}
          >
            Hacer Otra Postulación
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <Briefcase size={48} style={{ color: '#7c3aed' }} />
        <h1 style={styles.title}>Formulario de Postulación</h1>
        <p style={styles.subtitle}>Complete todos los pasos para postularse a una vacante</p>
      </div>

      {/* Indicador de Pasos */}
      <div style={styles.stepsContainer}>
        {[1, 2, 3, 4, 5].map((num) => (
          <div key={num} style={styles.stepItem}>
            <div style={{
              ...styles.stepCircle,
              backgroundColor: paso >= num ? '#7c3aed' : '#e5e7eb',
              color: paso >= num ? 'white' : '#6b7280'
            }}>
              {num}
            </div>
            <span style={styles.stepLabel}>
              {num === 1 && 'Datos Personales'}
              {num === 2 && 'Educación'}
              {num === 3 && 'Experiencia'}
              {num === 4 && 'Documentos'}
              {num === 5 && 'Confirmar'}
            </span>
          </div>
        ))}
      </div>

      {/* Error */}
      {error && (
        <div style={styles.errorBanner}>
          <AlertCircle size={20} />
          <span>{error}</span>
          <button onClick={() => setError(null)} style={styles.closeButton}>
            <X size={16} />
          </button>
        </div>
      )}

      {/* Formulario */}
      <form onSubmit={handleSubmit} style={styles.form}>
        {/* Paso 1: Datos Personales */}
        {paso === 1 && (
          <Paso1
            datos={datos}
            handleChange={handleChange}
            vacantes={vacantes}
          />
        )}

        {/* Paso 2: Educación */}
        {paso === 2 && (
          <Paso2
            datos={datos}
            handleChange={handleChange}
          />
        )}

        {/* Paso 3: Experiencia */}
        {paso === 3 && (
          <Paso3
            datos={datos}
            handleChange={handleChange}
          />
        )}

        {/* Paso 4: Documentos */}
        {paso === 4 && (
          <Paso4
            archivos={archivos}
            handleFileChange={handleFileChange}
          />
        )}

        {/* Paso 5: Confirmación */}
        {paso === 5 && (
          <Paso5
            datos={datos}
            archivos={archivos}
            vacantes={vacantes}
          />
        )}

        {/* Botones de Navegación */}
        <div style={styles.buttonContainer}>
          {paso > 1 && (
            <button
              type="button"
              onClick={pasoAnterior}
              style={styles.buttonSecondary}
            >
              <ArrowLeft size={20} />
              Anterior
            </button>
          )}
          
          {paso < 5 ? (
            <button
              type="button"
              onClick={siguientePaso}
              style={styles.button}
            >
              Siguiente
              <ArrowRight size={20} />
            </button>
          ) : (
            <button
              type="submit"
              disabled={enviando}
              style={{
                ...styles.button,
                backgroundColor: enviando ? '#9ca3af' : '#10b981',
                cursor: enviando ? 'not-allowed' : 'pointer'
              }}
            >
              {enviando ? (
                <>
                  <Loader size={20} style={{ animation: 'spin 1s linear infinite' }} />
                  Enviando...
                </>
              ) : (
                <>
                  <CheckCircle size={20} />
                  Enviar Postulación
                </>
              )}
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

// Componentes de cada paso
