import React, { useState, useEffect } from 'react';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts'; // âœ… AGREGADO
import { Play, Download, Save, TrendingUp, Users, Building, FileText, DollarSign } from 'lucide-react';
import { useAuth } from '../../../context/AuthContext'; // âœ… AGREGADO

// âœ… AGREGADO: Definir API_URL
const API_URL = process.env.REACT_APP_API_URL || 'http://192.168.0.239:5000/api';

const ReportesVisualModule = () => {
  const { getStoredToken } = useAuth(); // âœ… AGREGADO
  
  const [tipoReporte, setTipoReporte] = useState('');
  const [configuracion, setConfiguracion] = useState({});
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [metricas, setMetricas] = useState(null);

  // Cargar mÃ©tricas al inicio
  useEffect(() => {
    cargarMetricas();
  }, []);

  const cargarMetricas = async () => {
    try {
      const response = await fetch(`${API_URL}/reportes/metricas`, {
        headers: { Authorization: `Bearer ${getStoredToken()}` }
      });
      const result = await response.json();
      if (result.success) {
        setMetricas(result.data);
      }
    } catch (error) {
      console.error('Error cargando mÃ©tricas:', error);
    }
  };

  const TIPOS_REPORTES = [
    {
      id: 'empleados',
      nombre: 'Reporte de Empleados',
      descripcion: 'InformaciÃ³n detallada del personal',
      icono: Users,
      color: 'blue',
      configuraciones: [
        {
          campo: 'campos',
          label: 'InformaciÃ³n a mostrar',
          tipo: 'multiselect',
          opciones: [
            { value: 'nombre', label: 'Nombre' },
            { value: 'apellido', label: 'Apellido' },
            { value: 'email', label: 'Email' },
            { value: 'cargo', label: 'Cargo' },
            { value: 'salario', label: 'Salario' },
            { value: 'fechaIngreso', label: 'Fecha de Ingreso' },
            { value: 'departamento', label: 'Departamento' },
            { value: 'estado', label: 'Estado' },
            { value: 'cedula', label: 'CÃ©dula' }
          ],
          default: ['nombre', 'apellido', 'cargo', 'departamento']
        },
        {
          campo: 'filtros.estado',
          label: 'Estado del empleado',
          tipo: 'select',
          opciones: [
            { value: 'todos', label: 'Todos' },
            { value: 'activo', label: 'Solo Activos' },
            { value: 'inactivo', label: 'Solo Inactivos' }
          ],
          default: 'activo'
        },
        {
          campo: 'filtros.departamentoId',
          label: 'Departamento',
          tipo: 'select-departamentos',
          default: null
        },
        {
          campo: 'ordenamiento.campo',
          label: 'Ordenar por',
          tipo: 'select',
          opciones: [
            { value: 'nombre', label: 'Nombre' },
            { value: 'apellido', label: 'Apellido' },
            { value: 'fechaIngreso', label: 'Fecha de Ingreso' },
            { value: 'salario', label: 'Salario' }
          ],
          default: 'nombre'
        },
        {
          campo: 'ordenamiento.direccion',
          label: 'DirecciÃ³n',
          tipo: 'select',
          opciones: [
            { value: 'ASC', label: 'Ascendente (A-Z, 0-9)' },
            { value: 'DESC', label: 'Descendente (Z-A, 9-0)' }
          ],
          default: 'ASC'
        }
      ]
    },
    {
      id: 'departamentos',
      nombre: 'Reporte de Departamentos',
      descripcion: 'AnÃ¡lisis por departamento',
      icono: Building,
      color: 'green',
      configuraciones: [
        {
          campo: 'incluirEstadisticas',
          label: 'Â¿Incluir estadÃ­sticas de empleados?',
          tipo: 'checkbox',
          default: true
        },
        {
          campo: 'incluirEmpleados',
          label: 'Â¿Incluir lista de empleados?',
          tipo: 'checkbox',
          default: false
        }
      ]
    },
    {
      id: 'vacantes',
      nombre: 'Reporte de Vacantes',
      descripcion: 'Estado de vacantes y postulaciones',
      icono: FileText,
      color: 'purple',
      configuraciones: [
        {
          campo: 'estado',
          label: 'Estado de vacantes',
          tipo: 'select',
          opciones: [
            { value: 'Todas', label: 'Todas' },
            { value: 'Abierta', label: 'Solo Abiertas' },
            { value: 'Cerrada', label: 'Solo Cerradas' }
          ],
          default: 'Abierta'
        },
        {
          campo: 'incluirPostulaciones',
          label: 'Â¿Incluir estadÃ­sticas de postulaciones?',
          tipo: 'checkbox',
          default: true
        },
        {
          campo: 'fechaDesde',
          label: 'Desde',
          tipo: 'date',
          default: null
        },
        {
          campo: 'fechaHasta',
          label: 'Hasta',
          tipo: 'date',
          default: null
        }
      ]
    },
    {
      id: 'nomina',
      nombre: 'Reporte de NÃ³mina',
      descripcion: 'Severance, salarios y aumentos',
      icono: DollarSign,
      color: 'orange',
      configuraciones: [
        {
          campo: 'tipo',
          label: 'Tipo de reporte',
          tipo: 'select',
          opciones: [
            { value: 'severance', label: 'CÃ¡lculo de Severance' },
            { value: 'salarios', label: 'AnÃ¡lisis de Salarios' },
            { value: 'aumentos', label: 'Historial de Aumentos' }
          ],
          default: 'severance'
        },
        {
          campo: 'departamentoId',
          label: 'Departamento',
          tipo: 'select-departamentos',
          default: null
        },
        {
          campo: 'fechaReferencia',
          label: 'Fecha de referencia',
          tipo: 'date',
          default: new Date().toISOString().split('T')[0]
        }
      ]
    }
  ];

  const ejecutarReporte = async () => {
    if (!tipoReporte) {
      alert('Selecciona un tipo de reporte');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/reportes/${tipoReporte}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${getStoredToken()}`
        },
        body: JSON.stringify(configuracion)
      });

      const result = await response.json();
      if (result.success) {
        setData(result.data);
      } else {
        alert('Error generando reporte: ' + (result.error || 'Error desconocido'));
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error ejecutando reporte');
    } finally {
      setLoading(false);
    }
  };

  const exportarReporte = async (formato) => {
    if (data.length === 0) {
      alert('No hay datos para exportar');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/reportes/export`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${getStoredToken()}`
        },
        body: JSON.stringify({
          formato,
          data,
          nombre: `reporte_${tipoReporte}_${new Date().toISOString().split('T')[0]}`
        })
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `reporte_${tipoReporte}_${new Date().toISOString().split('T')[0]}.${formato}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error exportando:', error);
      alert('Error al exportar');
    }
  };

  const reporteSeleccionado = TIPOS_REPORTES.find(r => r.id === tipoReporte);

  // Funciones auxiliares
  function obtenerValorConfig(config, campo) {
    const campos = campo.split('.');
    let valor = config;
    for (const c of campos) {
      valor = valor?.[c];
    }
    return valor;
  }

  function actualizarConfiguracion(campo, valor) {
    const campos = campo.split('.');
    const nuevaConfig = { ...configuracion };
    
    let ref = nuevaConfig;
    for (let i = 0; i < campos.length - 1; i++) {
      if (!ref[campos[i]]) ref[campos[i]] = {};
      ref = ref[campos[i]];
    }
    
    ref[campos[campos.length - 1]] = valor;
    setConfiguracion(nuevaConfig);
  }

  function formatearNombreColumna(nombre) {
    return nombre
      .replace(/([A-Z])/g, ' $1')
      .replace(/_/g, ' ')
      .replace(/^./, str => str.toUpperCase())
      .trim();
  }

  function formatearValor(key, value) {
    if (value === null || value === undefined) return '-';
    
    // Fechas
    if (key.toLowerCase().includes('fecha') && value) {
      return new Date(value).toLocaleDateString('es-DO');
    }
    
    // Moneda
    if (key.toLowerCase().includes('salario') || 
        key.toLowerCase().includes('preaviso') ||
        key.toLowerCase().includes('cesantia') ||
        key.toLowerCase().includes('vacaciones') ||
        key.toLowerCase().includes('regalia') ||
        key.toLowerCase().includes('total') ||
        key.toLowerCase().includes('presupuesto')) {
      return `$${parseFloat(value).toLocaleString('es-DO', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    }
    
    // NÃºmeros
    if (typeof value === 'number') {
      return value.toLocaleString('es-DO');
    }
    
    return value;
  }

  function calcularTotalesSeverance(data) {
    const totales = {
      Preaviso: 0,
      Cesantia: 0,
      Vacaciones: 0,
      Regalia: 0,
      Total: 0
    };

    data.forEach(emp => {
      totales.Preaviso += emp.Preaviso || 0;
      totales.Cesantia += emp.Cesantia || 0;
      totales.Vacaciones += emp.Vacaciones || 0;
      totales.Regalia += emp.Regalia || 0;
      totales.Total += emp.Total || 0;
    });

    return (
      <>
        {Object.entries(totales).map(([key, value]) => (
          <div key={key}>
            <div className="text-xs text-blue-700 font-medium">{key}</div>
            <div className="text-lg font-bold text-blue-900">
              ${value.toLocaleString('es-DO', { minimumFractionDigits: 2 })}
            </div>
          </div>
        ))}
      </>
    );
  }

  return (
    <div className="p-6 max-w-7xl mx-auto bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">ðŸ“Š Reportes RRHH</h1>
        <p className="text-gray-600">Sistema de reporterÃ­a visual - Sin necesidad de SQL</p>
      </div>

      {/* Dashboard de MÃ©tricas */}
      {metricas && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-sm p-6 border-l-4 border-blue-500">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Total Empleados</p>
                <p className="text-3xl font-bold text-gray-900">{metricas.totalEmpleados}</p>
              </div>
              <Users className="w-12 h-12 text-blue-500 opacity-20" />
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm p-6 border-l-4 border-green-500">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Contrataciones (30d)</p>
                <p className="text-3xl font-bold text-gray-900">{metricas.contratacionesUltimos30Dias}</p>
              </div>
              <TrendingUp className="w-12 h-12 text-green-500 opacity-20" />
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm p-6 border-l-4 border-purple-500">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Salario Promedio</p>
                <p className="text-3xl font-bold text-gray-900">
                  ${metricas.salarioPromedio?.toLocaleString()}
                </p>
              </div>
              <DollarSign className="w-12 h-12 text-purple-500 opacity-20" />
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm p-6 border-l-4 border-orange-500">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Vacantes Abiertas</p>
                <p className="text-3xl font-bold text-gray-900">{metricas.vacantesAbiertas}</p>
              </div>
              <FileText className="w-12 h-12 text-orange-500 opacity-20" />
            </div>
          </div>
        </div>
      )}

      {/* SelecciÃ³n de Tipo de Reporte */}
      <div className="bg-white rounded-xl shadow-sm p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4 text-gray-800">Selecciona el tipo de reporte</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {TIPOS_REPORTES.map(tipo => {
            const Icon = tipo.icono;
            const isSelected = tipoReporte === tipo.id;
            return (
              <button
                key={tipo.id}
                onClick={() => {
                  setTipoReporte(tipo.id);
                  setConfiguracion({});
                  setData([]);
                }}
                className={`p-6 rounded-xl border-2 transition-all text-left ${
                  isSelected
                    ? `border-${tipo.color}-500 bg-${tipo.color}-50 shadow-lg`
                    : 'border-gray-200 hover:border-gray-300 hover:shadow-md'
                }`}
              >
                <Icon className={`w-10 h-10 mb-3 ${isSelected ? `text-${tipo.color}-600` : 'text-gray-400'}`} />
                <h3 className="font-semibold text-gray-900 mb-1">{tipo.nombre}</h3>
                <p className="text-sm text-gray-600">{tipo.descripcion}</p>
              </button>
            );
          })}
        </div>
      </div>

      {/* ConfiguraciÃ³n del Reporte */}
      {reporteSeleccionado && (
        <div className="bg-white rounded-xl shadow-sm p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">
            Configurar {reporteSeleccionado.nombre}
          </h2>
          
          <div className="space-y-6">
            {reporteSeleccionado.configuraciones.map((config, index) => (
              <ConfiguracionCampo
                key={index}
                config={config}
                valor={obtenerValorConfig(configuracion, config.campo)}
                onChange={(valor) => actualizarConfiguracion(config.campo, valor)}
              />
            ))}
          </div>

          <div className="flex gap-3 mt-6 pt-6 border-t">
            <button
              onClick={ejecutarReporte}
              disabled={loading}
              className={`flex-1 bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-all flex items-center justify-center gap-2 font-medium shadow-lg`}
            >
              <Play className="w-5 h-5" />
              {loading ? 'Generando...' : 'Generar Reporte'}
            </button>
            
            {data.length > 0 && (
              <>
                <button
                  onClick={() => exportarReporte('excel')}
                  className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition-all flex items-center gap-2"
                >
                  <Download className="w-5 h-5" />
                  Excel
                </button>
                <button
                  onClick={() => exportarReporte('pdf')}
                  className="bg-red-600 text-white px-6 py-3 rounded-lg hover:bg-red-700 transition-all flex items-center gap-2"
                >
                  <Download className="w-5 h-5" />
                  PDF
                </button>
                <button
                  onClick={() => exportarReporte('csv')}
                  className="bg-orange-600 text-white px-6 py-3 rounded-lg hover:bg-orange-700 transition-all flex items-center gap-2"
                >
                  <Download className="w-5 h-5" />
                  CSV
                </button>
              </>
            )}
          </div>
        </div>
      )}

      {/* Resultados */}
      {data.length > 0 && (
        <div className="bg-white rounded-xl shadow-sm overflow-hidden">
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 px-6 py-4">
            <h3 className="text-white font-semibold flex items-center gap-2">
              ðŸ“Š Resultados ({data.length} registros)
            </h3>
          </div>
          
          <div className="p-6">
            {/* GrÃ¡ficos si aplica */}
            {tipoReporte === 'departamentos' && configuracion.incluirEstadisticas && (
              <div className="mb-6">
                <GraficoBarras 
                  data={data}
                  xKey="Departamento"
                  yKey="TotalEmpleados"
                  titulo="Empleados por Departamento"
                />
              </div>
            )}

            {tipoReporte === 'nomina' && configuracion.tipo === 'salarios' && (
              <div className="mb-6">
                <GraficoBarras 
                  data={data}
                  xKey="Departamento"
                  yKey="SalarioPromedio"
                  titulo="Salario Promedio por Departamento"
                />
              </div>
            )}

            {/* Tabla de datos */}
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-gray-50">
                    {Object.keys(data[0] || {}).map((key) => (
                      <th key={key} className="border-b-2 border-gray-200 px-4 py-3 text-left text-sm font-semibold text-gray-700">
                        {formatearNombreColumna(key)}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {data.map((row, index) => (
                    <tr key={index} className="hover:bg-gray-50 transition-colors">
                      {Object.entries(row).map(([key, value], cellIndex) => (
                        <td key={cellIndex} className="border-b border-gray-100 px-4 py-3 text-sm text-gray-900">
                          {formatearValor(key, value)}
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Resumen si es reporte de nÃ³mina */}
            {tipoReporte === 'nomina' && configuracion.tipo === 'severance' && (
              <div className="mt-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
                <h4 className="font-semibold text-blue-900 mb-2">ðŸ’° Resumen Total</h4>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                  {calcularTotalesSeverance(data)}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Estado vacÃ­o */}
      {!tipoReporte && (
        <div className="bg-white rounded-xl shadow-sm p-12 text-center">
          <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <FileText className="w-10 h-10 text-gray-400" />
          </div>
          <h3 className="text-xl font-semibold text-gray-700 mb-2">
            Selecciona un tipo de reporte para comenzar
          </h3>
          <p className="text-gray-500">
            Elige uno de los tipos de reporte disponibles arriba
          </p>
        </div>
      )}
    </div>
  );
};

// ============================================
// COMPONENTE: Campo de ConfiguraciÃ³n
// ============================================

const ConfiguracionCampo = ({ config, valor, onChange }) => {
  const [departamentos, setDepartamentos] = useState([]);
  const { getStoredToken } = useAuth(); // âœ… AGREGADO

  useEffect(() => {
    if (config.tipo === 'select-departamentos') {
      cargarDepartamentos();
    }
  }, [config.tipo]);

  const cargarDepartamentos = async () => {
    try {
      const response = await fetch(`${API_URL}/departamentos`, {
        headers: { Authorization: `Bearer ${getStoredToken()}` }
      });
      const data = await response.json();
      setDepartamentos(data);
    } catch (error) {
      console.error('Error cargando departamentos:', error);
    }
  };

  // Multiselect
  if (config.tipo === 'multiselect') {
    const valoresSeleccionados = valor || config.default || [];
    
    return (
      <div>
        <label className="block font-medium text-gray-700 mb-3">{config.label}</label>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
          {config.opciones.map(opcion => {
            const isSelected = valoresSeleccionados.includes(opcion.value);
            return (
              <button
                key={opcion.value}
                onClick={() => {
                  const nuevosValores = isSelected
                    ? valoresSeleccionados.filter(v => v !== opcion.value)
                    : [...valoresSeleccionados, opcion.value];
                  onChange(nuevosValores);
                }}
                className={`p-3 rounded-lg border-2 text-sm text-left transition-all ${
                  isSelected
                    ? 'border-blue-500 bg-blue-50 text-blue-700'
                    : 'border-gray-200 hover:border-blue-300 text-gray-700'
                }`}
              >
                <div className="flex items-center justify-between">
                  <span>{opcion.label}</span>
                  {isSelected && (
                    <span className="text-blue-600 font-bold">âœ“</span>
                  )}
                </div>
              </button>
            );
          })}
        </div>
        <p className="text-xs text-gray-500 mt-2">
          {valoresSeleccionados.length} seleccionado(s)
        </p>
      </div>
    );
  }

  // Select simple
  if (config.tipo === 'select') {
    return (
      <div>
        <label className="block font-medium text-gray-700 mb-2">{config.label}</label>
        <select
          value={valor || config.default}
          onChange={(e) => onChange(e.target.value)}
          className="w-full border-2 border-gray-300 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        >
          {config.opciones.map(opcion => (
            <option key={opcion.value} value={opcion.value}>
              {opcion.label}
            </option>
          ))}
        </select>
      </div>
    );
  }

  // Select de departamentos
  if (config.tipo === 'select-departamentos') {
    return (
      <div>
        <label className="block font-medium text-gray-700 mb-2">{config.label}</label>
        <select
          value={valor || ''}
          onChange={(e) => onChange(e.target.value || null)}
          className="w-full border-2 border-gray-300 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">Todos los departamentos</option>
          {departamentos.map(dept => (
            <option key={dept.DepartamentoID} value={dept.DepartamentoID}>
              {dept.Nombre}
            </option>
          ))}
        </select>
      </div>
    );
  }

  // Checkbox
  if (config.tipo === 'checkbox') {
    return (
      <div className="flex items-center">
        <input
          type="checkbox"
          checked={valor !== undefined ? valor : config.default}
          onChange={(e) => onChange(e.target.checked)}
          className="w-5 h-5 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
        />
        <label className="ml-3 font-medium text-gray-700">{config.label}</label>
      </div>
    );
  }

  // Date
  if (config.tipo === 'date') {
    return (
      <div>
        <label className="block font-medium text-gray-700 mb-2">{config.label}</label>
        <input
          type="date"
          value={valor || config.default || ''}
          onChange={(e) => onChange(e.target.value)}
          className="w-full border-2 border-gray-300 rounded-lg px-4 py-3 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
    );
  }

  return null;
};

