import React, { useState, useEffect } from 'react';
import { X, Save, AlertCircle } from 'lucide-react';
import * as accionesPersonalApi from '../../../api/accionesPersonalApi';

const NuevaAccion = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    empleado_id: '',
    tipo_accion: '',
    fecha_efectiva: '',
    departamento_nuevo: '',
    puesto_nuevo: '',
    salario_nuevo: '',
    motivo: '',
    observaciones: ''
  });

  const [empleados, setEmpleados] = useState([]);
  const [departamentos, setDepartamentos] = useState([]);
  const [puestos, setPuestos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    cargarDatosIniciales();
  }, []);

  const cargarDatosIniciales = async () => {
    try {
      // Cargar empleados activos
      const resEmpleados = await fetch('http://localhost:5000/api/empleados', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const dataEmpleados = await resEmpleados.json();
      setEmpleados(dataEmpleados.empleados || []);

      // Cargar departamentos
      const resDepartamentos = await fetch('http://localhost:5000/api/departamentos', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const dataDepartamentos = await resDepartamentos.json();
      setDepartamentos(dataDepartamentos.departamentos || []);

      // Cargar puestos
      const resPuestos = await fetch('http://localhost:5000/api/puestos', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const dataPuestos = await resPuestos.json();
      setPuestos(dataPuestos.puestos || []);

    } catch (error) {
      console.error('Error al cargar datos:', error);
      setError('Error al cargar los datos necesarios');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const resultado = await accionesPersonalApi.crearAccion(formData);
      
      if (resultado.success) {
        onSuccess();
        onClose();
      } else {
        setError(resultado.message || 'Error al crear la acción');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Error al crear la acción de personal');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '1rem'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        maxWidth: '800px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'hidden',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
      }}>
        {/* Header */}
        <div style={{
          padding: '1.5rem',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            Nueva Acción de Personal
          </h2>
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              padding: '0.5rem',
              color: '#6b7280'
            }}
          >
            <X size={24} />
          </button>
        </div>

        {/* Error Alert */}
        {error && (
          <div style={{
            margin: '1rem 1.5rem',
            padding: '1rem',
            background: '#fee2e2',
            border: '1px solid #fecaca',
            borderRadius: '8px',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            <AlertCircle size={20} color="#dc2626" />
            <span style={{ color: '#dc2626', fontSize: '0.875rem' }}>{error}</span>
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit} style={{ overflowY: 'auto', maxHeight: 'calc(90vh - 140px)' }}>
          <div style={{ padding: '1.5rem', display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
            
            {/* Empleado */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '0.5rem'
              }}>
                Empleado <span style={{ color: '#ef4444' }}>*</span>
              </label>
              <select
                name="empleado_id"
                value={formData.empleado_id}
                onChange={handleChange}
                required
                style={{
                  width: '100%',
                  padding: '0.625rem 0.75rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '0.875rem',
                  outline: 'none'
                }}
              >
                <option value="">Seleccione un empleado</option>
                {empleados.map(emp => (
                  <option key={emp.EmpleadoID} value={emp.EmpleadoID}>
                    {emp.Nombre} {emp.Apellido} - {emp.CEDULA}
                  </option>
                ))}
              </select>
            </div>

            {/* Tipo de Acción */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '0.5rem'
              }}>
                Tipo de Acción <span style={{ color: '#ef4444' }}>*</span>
              </label>
              <select
                name="tipo_accion"
                value={formData.tipo_accion}
                onChange={handleChange}
                required
                style={{
                  width: '100%',
                  padding: '0.625rem 0.75rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '0.875rem',
                  outline: 'none'
                }}
              >
                <option value="">Seleccione el tipo</option>
                <option value="INGRESO">Ingreso de Personal</option>
                <option value="REINGRESO">Reingreso</option>
                <option value="PROMOCION">Promoción</option>
                <option value="CAMBIO_DEPTO">Cambio de Departamento</option>
                <option value="CAMBIO_PUESTO">Cambio de Puesto</option>
                <option value="AJUSTE_SALARIAL">Ajuste Salarial</option>
                <option value="TRANSFERENCIA">Transferencia</option>
                <option value="CAMBIO_SUPERVISOR">Cambio de Supervisor</option>
                <option value="CAMBIO_JORNADA">Cambio de Jornada</option>
                <option value="TERMINACION">Terminación</option>
              </select>
            </div>

            {/* Fecha Efectiva */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '0.5rem'
              }}>
                Fecha Efectiva <span style={{ color: '#ef4444' }}>*</span>
              </label>
              <input
                type="date"
                name="fecha_efectiva"
                value={formData.fecha_efectiva}
                onChange={handleChange}
                required
                style={{
                  width: '100%',
                  padding: '0.625rem 0.75rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '0.875rem',
                  outline: 'none'
                }}
              />
            </div>

            {/* Grid para campos condicionales */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '1rem' }}>
              
              {/* Departamento Nuevo */}
              {['CAMBIO_DEPTO', 'PROMOCION', 'TRANSFERENCIA'].includes(formData.tipo_accion) && (
                <div>
                  <label style={{
                    display: 'block',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    color: '#374151',
                    marginBottom: '0.5rem'
                  }}>
                    Nuevo Departamento
                  </label>
                  <select
                    name="departamento_nuevo"
                    value={formData.departamento_nuevo}
                    onChange={handleChange}
                    style={{
                      width: '100%',
                      padding: '0.625rem 0.75rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                  >
                    <option value="">Seleccione</option>
                    {departamentos.map(dept => (
                      <option key={dept.DepartamentoID} value={dept.DepartamentoID}>
                        {dept.NombreDepartamento}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              {/* Puesto Nuevo */}
              {['CAMBIO_PUESTO', 'PROMOCION'].includes(formData.tipo_accion) && (
                <div>
                  <label style={{
                    display: 'block',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    color: '#374151',
                    marginBottom: '0.5rem'
                  }}>
                    Nuevo Puesto
                  </label>
                  <select
                    name="puesto_nuevo"
                    value={formData.puesto_nuevo}
                    onChange={handleChange}
                    style={{
                      width: '100%',
                      padding: '0.625rem 0.75rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                  >
                    <option value="">Seleccione</option>
                    {puestos.map(puesto => (
                      <option key={puesto.PuestoID} value={puesto.PuestoID}>
                        {puesto.NombrePuesto}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              {/* Salario Nuevo */}
              {['AJUSTE_SALARIAL', 'PROMOCION'].includes(formData.tipo_accion) && (
                <div>
                  <label style={{
                    display: 'block',
                    fontSize: '0.875rem',
                    fontWeight: '600',
                    color: '#374151',
                    marginBottom: '0.5rem'
                  }}>
                    Nuevo Salario
                  </label>
                  <input
                    type="number"
                    name="salario_nuevo"
                    value={formData.salario_nuevo}
                    onChange={handleChange}
                    step="0.01"
                    placeholder="0.00"
                    style={{
                      width: '100%',
                      padding: '0.625rem 0.75rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '0.875rem',
                      outline: 'none'
                    }}
                  />
                </div>
              )}
            </div>

            {/* Motivo */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '0.5rem'
              }}>
                Motivo <span style={{ color: '#ef4444' }}>*</span>
              </label>
              <textarea
                name="motivo"
                value={formData.motivo}
                onChange={handleChange}
                required
                rows="3"
                placeholder="Describa el motivo de la acción..."
                style={{
                  width: '100%',
                  padding: '0.625rem 0.75rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '0.875rem',
                  outline: 'none',
                  resize: 'vertical'
                }}
              />
            </div>

            {/* Observaciones */}
            <div>
              <label style={{
                display: 'block',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '0.5rem'
              }}>
                Observaciones
              </label>
              <textarea
                name="observaciones"
                value={formData.observaciones}
                onChange={handleChange}
                rows="3"
                placeholder="Observaciones adicionales..."
                style={{
                  width: '100%',
                  padding: '0.625rem 0.75rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '0.875rem',
                  outline: 'none',
                  resize: 'vertical'
                }}
              />
            </div>

          </div>

          {/* Footer con botones */}
          <div style={{
            padding: '1rem 1.5rem',
            borderTop: '1px solid #e5e7eb',
            display: 'flex',
            justifyContent: 'flex-end',
            gap: '1rem',
            background: '#f9fafb'
          }}>
            <button
              type="button"
              onClick={onClose}
              disabled={loading}
              style={{
                padding: '0.625rem 1.25rem',
                background: 'white',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: '#374151',
                cursor: 'pointer'
              }}
            >
              Cancelar
            </button>
            <button
              type="submit"
              disabled={loading}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '0.625rem 1.25rem',
                background: loading ? '#9ca3af' : '#3b82f6',
                border: 'none',
                borderRadius: '8px',
                fontSize: '0.875rem',
                fontWeight: '600',
                color: 'white',
                cursor: loading ? 'not-allowed' : 'pointer'
              }}
            >
              <Save size={18} />
              {loading ? 'Guardando...' : 'Guardar Acción'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NuevaAccion;
