// Vista de Vacantes Disponibles
const VistaVacantes = ({ vacantes, userRole, onPostularse, onVerDetalles }) => {
  const [filtro, setFiltro] = useState('');
  const [filtroEstado, setFiltroEstado] = useState('');

  const vacantesFiltradas = vacantes.filter(v => 
    (filtro === '' || 
     v.titulo?.toLowerCase().includes(filtro.toLowerCase()) ||
     v.departamento?.toLowerCase().includes(filtro.toLowerCase())) &&
    (filtroEstado === '' || v.estado === filtroEstado)
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '2rem' }}>
        <div>
          <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            Vacantes Disponibles
          </h2>
          <p style={{ color: '#6b7280', margin: '0.5rem 0 0 0' }}>
            Explora las oportunidades laborales disponibles en la organización
          </p>
        </div>
        
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <div style={{ position: 'relative' }}>
            <Search size={18} style={{ 
              position: 'absolute', 
              left: '0.75rem', 
              top: '50%', 
              transform: 'translateY(-50%)', 
              color: '#9ca3af' 
            }} />
            <input
              type="text"
              placeholder="Buscar vacantes..."
              value={filtro}
              onChange={(e) => setFiltro(e.target.value)}
              style={{
                paddingLeft: '2.5rem',
                paddingRight: '1rem',
                paddingTop: '0.75rem',
                paddingBottom: '0.75rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.5rem',
                outline: 'none',
                fontSize: '0.875rem',
                minWidth: '200px'
              }}
            />
          </div>
          
          <select
            value={filtroEstado}
            onChange={(e) => setFiltroEstado(e.target.value)}
            style={{
              padding: '0.75rem',
              border: '1px solid #d1d5db',
              borderRadius: '0.5rem',
              fontSize: '0.875rem',
              minWidth: '150px'
            }}
          >
            <option value="">Todos los estados</option>
            <option value="VACANTE_PUBLICADA">Publicada</option>
            <option value="VACANTE_CERRADA">Cerrada</option>
          </select>
        </div>
      </div>

      <div style={{ display: 'grid', gap: '1.5rem' }}>
        {vacantesFiltradas.map(vacante => (
          <TarjetaVacante 
            key={vacante.id} 
            vacante={vacante} 
            userRole={userRole}
            onPostularse={onPostularse}
            onVerDetalles={onVerDetalles}
          />
        ))}
      </div>

      {vacantesFiltradas.length === 0 && (
        <div style={{ 
          textAlign: 'center', 
          padding: '4rem 2rem',
          backgroundColor: '#f8fafc',
          borderRadius: '0.75rem',
          border: '2px dashed #d1d5db'
        }}>
          <Briefcase size={64} style={{ margin: '0 auto 1.5rem', color: '#9ca3af' }} />
          <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
            No hay vacantes disponibles
          </h3>
          <p style={{ color: '#6b7280', maxWidth: '400px', margin: '0 auto' }}>
            {filtro || filtroEstado 
              ? 'No se encontraron vacantes que coincidan con los filtros aplicados' 
              : 'No hay vacantes publicadas en este momento. Revisa más tarde o contacta a RRHH.'}
          </p>
        </div>
      )}
    </div>
  );
};

const TarjetaVacante = ({ vacante, userRole, onPostularse, onVerDetalles }) => {
  const estadoConfig = ESTADOS_SOLICITUD[vacante.estado] || { color: '#6b7280', bg: '#f3f4f6' };
  const puedePostularse = userRole === 'colaborador' && vacante.estado === 'VACANTE_PUBLICADA';

  return (
    <div style={{
      border: '1px solid #e2e8f0',
      borderRadius: '0.75rem',
      padding: '1.5rem',
      backgroundColor: 'white',
      transition: 'all 0.2s',
      cursor: 'pointer'
    }}
    onMouseEnter={(e) => {
      e.currentTarget.style.boxShadow = '0 10px 15px -3px rgba(0, 0, 0, 0.1)';
      e.currentTarget.style.transform = 'translateY(-2px)';
    }}
    onMouseLeave={(e) => {
      e.currentTarget.style.boxShadow = 'none';
      e.currentTarget.style.transform = 'translateY(0)';
    }}>
      
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '1rem' }}>
        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.5rem' }}>
            <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', margin: 0 }}>
              {vacante.titulo}
            </h3>
            <span style={{
              backgroundColor: estadoConfig.bg,
              color: estadoConfig.color,
              padding: '0.25rem 0.75rem',
              borderRadius: '1rem',
              fontSize: '0.75rem',
              fontWeight: '500'
            }}>
              {estadoConfig.label}
            </span>
          </div>
          
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: '1.5rem', 
            fontSize: '0.875rem', 
            color: '#6b7280', 
            marginBottom: '1rem' 
          }}>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Building size={16} />
              {vacante.departamento}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <MapPin size={16} />
              {vacante.ubicacion || 'Santo Domingo'}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Users size={16} />
              {vacante.totalPostulaciones || 0} postulaciones
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Calendar size={16} />
              {new Date(vacante.fechaPublicacion).toLocaleDateString()}
            </span>
          </div>
        </div>
        
        <div style={{ textAlign: 'right', marginLeft: '1rem' }}>
          <div style={{ fontSize: '1.25rem', fontWeight: '600', color: '#059669', marginBottom: '0.25rem' }}>
            RD${vacante.salarioMinimo?.toLocaleString()} - RD${vacante.salarioMaximo?.toLocaleString()}
          </div>
          <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
            {vacante.modalidad || 'Presencial'}
          </div>
        </div>
      </div>

      <p style={{ 
        color: '#374151', 
        marginBottom: '1.5rem', 
        lineHeight: '1.6',
        display: '-webkit-box',
        WebkitLineClamp: 2,
        WebkitBoxOrient: 'vertical',
        overflow: 'hidden'
      }}>
        {vacante.descripcion}
      </p>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        paddingTop: '1rem', 
        borderTop: '1px solid #f3f4f6' 
      }}>
        <button
          onClick={() => onVerDetalles(vacante)}
          style={{
            backgroundColor: '#f8fafc',
            border: '1px solid #e2e8f0',
            color: '#4f46e5',
            padding: '0.5rem 1rem',
            borderRadius: '0.5rem',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            fontSize: '0.875rem',
            fontWeight: '500'
          }}
        >
          <Eye size={16} />
          Ver Detalles
        </button>
        
        {puedePostularse && (
          <button
            onClick={() => onPostularse(vacante)}
            style={{
              backgroundColor: '#4f46e5',
              color: 'white',
              padding: '0.5rem 1.5rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.875rem',
              fontWeight: '500'
            }}
          >
            <Send size={16} />
            Postularse
          </button>
        )}
      </div>
    </div>
  );
};


// Vista de Solicitudes
const VistaSolicitudes = ({ solicitudes, usuarioActual, onCrearSolicitud, onAprobar, onRechazar }) => {
  const [filtroEstado, setFiltroEstado] = useState('');

  const solicitudesFiltradas = solicitudes.filter(s => 
    !filtroEstado || s.estado === filtroEstado
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
        <div>
          <h2 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            {usuarioActual?.role === 'director' ? 'Mis Solicitudes' : 'Gestionar Solicitudes'}
          </h2>
          <p style={{ color: '#6b7280', margin: '0.5rem 0 0 0' }}>
            {usuarioActual?.role === 'director' ? 'Solicitudes de vacantes creadas' : 'Revisar y aprobar solicitudes'}
          </p>
        </div>
        
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <select
            value={filtroEstado}
            onChange={(e) => setFiltroEstado(e.target.value)}
            style={{
              padding: '0.5rem',
              border: '1px solid #d1d5db',
              borderRadius: '0.5rem',
              fontSize: '0.875rem'
            }}
          >
            <option value="">Todos los estados</option>
            <option value="Pendiente">Pendiente</option>
            <option value="Aprobada">Aprobada</option>
            <option value="Rechazada">Rechazada</option>
          </select>
          
          {usuarioActual?.role === 'director' && (
            <button
              onClick={onCrearSolicitud}
              style={{
                backgroundColor: '#3b82f6',
                color: 'white',
                padding: '0.5rem 1rem',
                borderRadius: '0.5rem',
                border: 'none',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                fontSize: '0.875rem'
              }}
            >
              <Plus size={16} />
              Nueva Solicitud
            </button>
          )}
        </div>
      </div>

      <div style={{ display: 'grid', gap: '1rem' }}>
        {solicitudesFiltradas.map(solicitud => (
          <TarjetaSolicitud 
            key={solicitud.id} 
            solicitud={solicitud}
            usuarioActual={usuarioActual}
            onAprobar={onAprobar}
            onRechazar={onRechazar}
          />
        ))}
      </div>

      {solicitudesFiltradas.length === 0 && (
        <div style={{ textAlign: 'center', padding: '3rem' }}>
          <FileText size={48} style={{ margin: '0 auto 1rem', color: '#9ca3af' }} />
          <h3 style={{ fontSize: '1.125rem', fontWeight: '500', color: '#1f2937', marginBottom: '0.5rem' }}>
            No hay solicitudes
          </h3>
          <p style={{ color: '#6b7280' }}>
            {filtroEstado ? `No hay solicitudes con estado "${filtroEstado}"` : 'No hay solicitudes creadas'}
          </p>
        </div>
      )}
    </div>
  );
};

const TarjetaSolicitud = ({ solicitud, usuarioActual, onAprobar, onRechazar }) => {
  const getEstadoColor = (estado) => {
    switch (estado) {
      case 'Pendiente': return { bg: '#fef3c7', text: '#92400e', border: '#f59e0b' };
      case 'Aprobada': return { bg: '#d1fae5', text: '#065f46', border: '#10b981' };
      case 'Rechazada': return { bg: '#fee2e2', text: '#991b1b', border: '#ef4444' };
      default: return { bg: '#f3f4f6', text: '#374151', border: '#9ca3af' };
    }
  };

  const estadoColor = getEstadoColor(solicitud.estado);

  return (
    <div style={{
      border: '1px solid #e5e7eb',
      borderRadius: '0.5rem',
      padding: '1.5rem',
      backgroundColor: 'white'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '1rem' }}>
        <div style={{ flex: 1 }}>
          <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: '#1f2937', margin: '0 0 0.5rem 0' }}>
            {solicitud.cargo}
          </h3>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', fontSize: '0.875rem', color: '#6b7280' }}>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Building size={14} />
              {solicitud.departamento}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <User size={14} />
              {solicitud.solicitante}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Calendar size={14} />
              {new Date(solicitud.fechaSolicitud).toLocaleDateString()}
            </span>
          </div>
        </div>
        
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span style={{
            backgroundColor: estadoColor.bg,
            color: estadoColor.text,
            border: `1px solid ${estadoColor.border}`,
            padding: '0.25rem 0.75rem',
            borderRadius: '1rem',
            fontSize: '0.75rem',
            fontWeight: '500'
          }}>
            {solicitud.estado}
          </span>
          
          <div style={{ fontSize: '1rem', fontWeight: '600', color: '#059669' }}>
            RD${solicitud.salarioMin?.toLocaleString()} - RD${solicitud.salarioMax?.toLocaleString()}
          </div>
        </div>
      </div>

      <p style={{ color: '#374151', marginBottom: '1rem', lineHeight: '1.5' }}>
        {solicitud.justificacion}
      </p>

      <div style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '1rem' }}>
        <strong>Modalidad:</strong> {solicitud.modalidad}
      </div>

      {usuarioActual?.role === 'rrhh' && solicitud.estado === 'Pendiente' && (
        <div style={{ display: 'flex', gap: '0.5rem', paddingTop: '1rem', borderTop: '1px solid #f3f4f6' }}>
          <button
            onClick={() => onAprobar(solicitud.id)}
            style={{
              backgroundColor: '#10b981',
              color: 'white',
              padding: '0.5rem 1rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.875rem'
            }}
          >
            <CheckCircle size={16} />
            Aprobar
          </button>
          
          <button
            onClick={() => onRechazar(solicitud.id)}
            style={{
              backgroundColor: '#ef4444',
              color: 'white',
              padding: '0.5rem 1rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.875rem'
            }}
          >
            <XCircle size={16} />
            Rechazar
          </button>
        </div>
      )}
    </div>
  );
};

// Vista de Postulaciones
const VistaPostulaciones = ({ postulaciones, usuarioActual }) => {
  const [filtroEstado, setFiltroEstado] = useState('');

  const postulacionesFiltradas = postulaciones.filter(p => 
    !filtroEstado || p.estado === filtroEstado
  );

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
        <div>
          <h2 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            {usuarioActual?.role === 'colaborador' ? 'Mis Postulaciones' : 'Gestionar Postulaciones'}
          </h2>
          <p style={{ color: '#6b7280', margin: '0.5rem 0 0 0' }}>
            {usuarioActual?.role === 'colaborador' ? 'Estado de tus postulaciones' : 'Revisar postulaciones recibidas'}
          </p>
        </div>
        
        <select
          value={filtroEstado}
          onChange={(e) => setFiltroEstado(e.target.value)}
          style={{
            padding: '0.5rem',
            border: '1px solid #d1d5db',
            borderRadius: '0.5rem',
            fontSize: '0.875rem'
          }}
        >
          <option value="">Todos los estados</option>
          <option value="Recibida">Recibida</option>
          <option value="En revisión">En revisión</option>
          <option value="Aprobada">Aprobada</option>
          <option value="Rechazada">Rechazada</option>
        </select>
      </div>

      <div style={{ display: 'grid', gap: '1rem' }}>
        {postulacionesFiltradas.map(postulacion => (
          <TarjetaPostulacion 
            key={postulacion.id} 
            postulacion={postulacion}
            usuarioActual={usuarioActual}
          />
        ))}
      </div>

      {postulacionesFiltradas.length === 0 && (
        <div style={{ textAlign: 'center', padding: '3rem' }}>
          <Send size={48} style={{ margin: '0 auto 1rem', color: '#9ca3af' }} />
          <h3 style={{ fontSize: '1.125rem', fontWeight: '500', color: '#1f2937', marginBottom: '0.5rem' }}>
            No hay postulaciones
          </h3>
          <p style={{ color: '#6b7280' }}>
            {filtroEstado ? `No hay postulaciones con estado "${filtroEstado}"` : 'No hay postulaciones registradas'}
          </p>
        </div>
      )}
    </div>
  );
};

const TarjetaPostulacion = ({ postulacion, usuarioActual }) => {
  const getEstadoColor = (estado) => {
    switch (estado) {
      case 'Recibida': return { bg: '#dbeafe', text: '#1e40af', border: '#3b82f6' };
      case 'En revisión': return { bg: '#fef3c7', text: '#92400e', border: '#f59e0b' };
      case 'Aprobada': return { bg: '#d1fae5', text: '#065f46', border: '#10b981' };
      case 'Rechazada': return { bg: '#fee2e2', text: '#991b1b', border: '#ef4444' };
      default: return { bg: '#f3f4f6', text: '#374151', border: '#9ca3af' };
    }
  };

  const estadoColor = getEstadoColor(postulacion.estado);

  return (
    <div style={{
      border: '1px solid #e5e7eb',
      borderRadius: '0.5rem',
      padding: '1.5rem',
      backgroundColor: 'white'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '1rem' }}>
        <div style={{ flex: 1 }}>
          <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: '#1f2937', margin: '0 0 0.5rem 0' }}>
            {postulacion.cargoVacante}
          </h3>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', fontSize: '0.875rem', color: '#6b7280' }}>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <User size={14} />
              {postulacion.nombre}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Mail size={14} />
              {postulacion.email}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Phone size={14} />
              {postulacion.telefono}
            </span>
          </div>
        </div>
        
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span style={{
            backgroundColor: estadoColor.bg,
            color: estadoColor.text,
            border: `1px solid ${estadoColor.border}`,
            padding: '0.25rem 0.75rem',
            borderRadius: '1rem',
            fontSize: '0.75rem',
            fontWeight: '500'
          }}>
            {postulacion.estado}
          </span>
          
          <span style={{
            backgroundColor: postulacion.tipoPostulante === 'Interno' ? '#dbeafe' : '#f3f4f6',
            color: postulacion.tipoPostulante === 'Interno' ? '#1e40af' : '#374151',
            padding: '0.25rem 0.75rem',
            borderRadius: '1rem',
            fontSize: '0.75rem',
            fontWeight: '500'
          }}>
            {postulacion.tipoPostulante}
          </span>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginBottom: '1rem' }}>
        <div>
          <h4 style={{ fontWeight: '500', color: '#1f2937', margin: '0 0 0.5rem 0', fontSize: '0.875rem' }}>Experiencia:</h4>
          <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>{postulacion.experiencia}</p>
        </div>
        <div>
          <h4 style={{ fontWeight: '500', color: '#1f2937', margin: '0 0 0.5rem 0', fontSize: '0.875rem' }}>Educación:</h4>
          <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>{postulacion.educacion}</p>
        </div>
        <div>
          <h4 style={{ fontWeight: '500', color: '#1f2937', margin: '0 0 0.5rem 0', fontSize: '0.875rem' }}>Expectativa Salarial:</h4>
          <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>RD${postulacion.expectativaSalarial?.toLocaleString()}</p>
        </div>
      </div>

      <div style={{ fontSize: '0.875rem', color: '#6b7280', paddingTop: '1rem', borderTop: '1px solid #f3f4f6' }}>
        <strong>Fecha de postulación:</strong> {new Date(postulacion.fechaPostulacion).toLocaleDateString()}
      </div>
    </div>
  );
};

// Modal para crear solicitud
const ModalSolicitud = ({ onClose, onGuardar, departamentos }) => {
  const [datos, setDatos] = useState({
    cargo: '',
    departamentoID: '',
    justificacion: '',
    salarioMin: '',
    salarioMax: '',
    modalidad: 'Presencial'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onGuardar(datos);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '0.5rem',
        padding: '2rem',
        maxWidth: '500px',
        width: '90%',
        maxHeight: '90vh',
        overflow: 'auto'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
          <h3 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>Nueva Solicitud de Vacante</h3>
          <button onClick={onClose} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '1rem' }}>
          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Cargo solicitado
            </label>
            <input
              type="text"
              value={datos.cargo}
              onChange={(e) => setDatos(prev => ({ ...prev, cargo: e.target.value }))}
              required
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem'
              }}
            />
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Departamento
            </label>
            <select
              value={datos.departamentoID}
              onChange={(e) => setDatos(prev => ({ ...prev, departamentoID: e.target.value }))}
              required
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem'
              }}
            >
              <option value="">Seleccionar departamento</option>
              {departamentos.map(dep => (
                <option key={dep.id} value={dep.id}>{dep.nombre}</option>
              ))}
            </select>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Salario mínimo (RD$)
              </label>
              <input
                type="number"
                value={datos.salarioMin}
                onChange={(e) => setDatos(prev => ({ ...prev, salarioMin: e.target.value }))}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Salario máximo (RD$)
              </label>
              <input
                type="number"
                value={datos.salarioMax}
                onChange={(e) => setDatos(prev => ({ ...prev, salarioMax: e.target.value }))}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Modalidad
            </label>
            <select
              value={datos.modalidad}
              onChange={(e) => setDatos(prev => ({ ...prev, modalidad: e.target.value }))}
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem'
              }}
            >
              <option value="Presencial">Presencial</option>
              <option value="Remoto">Remoto</option>
              <option value="Híbrido">Híbrido</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Justificación
            </label>
            <textarea
              value={datos.justificacion}
              onChange={(e) => setDatos(prev => ({ ...prev, justificacion: e.target.value }))}
              required
              rows={4}
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem',
                resize: 'vertical'
              }}
            />
          </div>

          <div style={{ display: 'flex', gap: '0.5rem', marginTop: '1rem' }}>
            <button
              type="submit"
              style={{
                flex: 1,
                backgroundColor: '#3b82f6',
                color: 'white',
                padding: '0.75rem',
                borderRadius: '0.25rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '0.875rem',
                fontWeight: '500'
              }}
            >
              Crear Solicitud
            </button>
            <button
              type="button"
              onClick={onClose}
              style={{
                flex: 1,
                backgroundColor: '#6b7280',
                color: 'white',
                padding: '0.75rem',
                borderRadius: '0.25rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '0.875rem',
                fontWeight: '500'
              }}
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Modal para postulación
const ModalPostulacion = ({ vacante, usuarioActual, onClose, onGuardar }) => {
  const [datos, setDatos] = useState({
    nombre: usuarioActual?.name || '',
    email: '',
    telefono: '',
    experiencia: '',
    educacion: '',
    expectativaSalarial: '',
    motivacion: '',
    cv: null
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onGuardar(datos);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setDatos(prev => ({ ...prev, cv: file }));
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '0.5rem',
        padding: '2rem',
        maxWidth: '600px',
        width: '90%',
        maxHeight: '90vh',
        overflow: 'auto'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
          <div>
            <h3 style={{ fontSize: '1.25rem', fontWeight: 'bold', color: '#1f2937', margin: '0 0 0.5rem 0' }}>
              Postularse a: {vacante?.cargo}
            </h3>
            <p style={{ color: '#6b7280', margin: 0, fontSize: '0.875rem' }}>
              {vacante?.departamento} • RD${vacante?.salarioMin?.toLocaleString()} - RD${vacante?.salarioMax?.toLocaleString()}
            </p>
          </div>
          <button onClick={onClose} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '1rem' }}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Nombre completo
              </label>
              <input
                type="text"
                value={datos.nombre}
                onChange={(e) => setDatos(prev => ({ ...prev, nombre: e.target.value }))}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Correo electrónico
              </label>
              <input
                type="email"
                value={datos.email}
                onChange={(e) => setDatos(prev => ({ ...prev, email: e.target.value }))}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Teléfono
              </label>
              <input
                type="tel"
                value={datos.telefono}
                onChange={(e) => setDatos(prev => ({ ...prev, telefono: e.target.value }))}
                required
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
                Expectativa salarial (RD$)
              </label>
              <input
                type="number"
                value={datos.expectativaSalarial}
                onChange={(e) => setDatos(prev => ({ ...prev, expectativaSalarial: e.target.value }))}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.25rem',
                  fontSize: '0.875rem'
                }}
              />
            </div>
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Experiencia laboral
            </label>
            <textarea
              value={datos.experiencia}
              onChange={(e) => setDatos(prev => ({ ...prev, experiencia: e.target.value }))}
              required
              rows={3}
              placeholder="Describe tu experiencia relevante para este puesto..."
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem',
                resize: 'vertical'
              }}
            />
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Formación académica
            </label>
            <textarea
              value={datos.educacion}
              onChange={(e) => setDatos(prev => ({ ...prev, educacion: e.target.value }))}
              required
              rows={2}
              placeholder="Incluye títulos, certificaciones y formación relevante..."
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem',
                resize: 'vertical'
              }}
            />
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Motivación
            </label>
            <textarea
              value={datos.motivacion}
              onChange={(e) => setDatos(prev => ({ ...prev, motivacion: e.target.value }))}
              required
              rows={3}
              placeholder="¿Por qué te interesa esta posición? ¿Qué te motiva a trabajar con nosotros?"
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #d1d5db',
                borderRadius: '0.25rem',
                fontSize: '0.875rem',
                resize: 'vertical'
              }}
            />
          </div>

          <div>
            <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', color: '#374151', marginBottom: '0.5rem' }}>
              Curriculum Vitae (PDF)
            </label>
            <div style={{ position: 'relative' }}>
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                style={{ display: 'none' }}
                id="cv-upload"
              />
              <label
                htmlFor="cv-upload"
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  padding: '0.75rem',
                  border: '2px dashed #d1d5db',
                  borderRadius: '0.25rem',
                  cursor: 'pointer',
                  fontSize: '0.875rem',
                  color: '#6b7280',
                  backgroundColor: '#f9fafb'
                }}
              >
                <Upload size={20} />
                {datos.cv ? datos.cv.name : 'Seleccionar archivo PDF'}
              </label>
            </div>
          </div>

          <div style={{ display: 'flex', gap: '0.5rem', marginTop: '1rem' }}>
            <button
              type="submit"
              style={{
                flex: 1,
                backgroundColor: '#3b82f6',
                color: 'white',
                padding: '0.75rem',
                borderRadius: '0.25rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '0.875rem',
                fontWeight: '500',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '0.5rem'
              }}
            >
              <Send size={16} />
              Enviar Postulación
            </button>
            <button
              type="button"
              onClick={onClose}
              style={{
                flex: 1,
                backgroundColor: '#6b7280',
                color: 'white',
                padding: '0.75rem',
                borderRadius: '0.25rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '0.875rem',
                fontWeight: '500'
              }}
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default VacantesModule;
