import React, { useState } from 'react';
import { Eye, CheckCircle, XCircle, FileText } from 'lucide-react';

const VistaAprobarSolicitudes = ({ solicitudes, onVerDetalles, onAprobar, onRechazar }) => {
  const [filtroEstado, setFiltroEstado] = useState('');

  const solicitudesFiltradas = solicitudes.filter(s => !filtroEstado || s.estado === filtroEstado);

  return (
    <div>
      <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '1rem' }}>
        Solicitudes para Aprobar
      </h2>

      <div style={{ display: 'grid', gap: '1rem' }}>
        {solicitudesFiltradas.map(solicitud => (
          <div
            key={solicitud.id}
            style={{
              border: '1px solid #e2e8f0',
              borderRadius: '0.75rem',
              padding: '1.5rem',
              backgroundColor: 'white',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}
          >
            <div>
              <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: '#1f2937', margin: 0 }}>
                {solicitud.cargo}
              </h3>
              <p style={{ color: '#6b7280', fontSize: '0.875rem' }}>
                {solicitud.solicitante} - {solicitud.departamento}
              </p>
            </div>

            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <button
                onClick={() => onVerDetalles(solicitud)}
                style={{
                  backgroundColor: '#f8fafc',
                  border: '1px solid #e2e8f0',
                  color: '#4f46e5',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                }}
              >
                <Eye size={16} />
                Ver Detalles
              </button>
              <button
                onClick={() => onAprobar(solicitud.id)}
                style={{
                  backgroundColor: '#16a34a',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                }}
              >
                <CheckCircle size={16} />
                Aprobar
              </button>
              <button
                onClick={() => onRechazar(solicitud.id)}
                style={{
                  backgroundColor: '#dc2626',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                }}
              >
                <XCircle size={16} />
                Rechazar
              </button>
            </div>
          </div>
        ))}
      </div>

      {solicitudesFiltradas.length === 0 && (
        <div style={{ textAlign: 'center', padding: '4rem', color: '#6b7280' }}>
          <FileText size={64} style={{ marginBottom: '1rem' }} />
          <p>No hay solicitudes para aprobar</p>
        </div>
      )}
    </div>
  );
};

export default VistaAprobarSolicitudes;
