// src/components/modules/Reportes/ReportesModule.jsx
import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import { useAuth } from "../../../context/AuthContext";

const API_URL = process.env.REACT_APP_API_URL || "http://192.168.0.239:5000/api";

// ===================== API CLIENT =====================
const apiClient = axios.create({ baseURL: API_URL, timeout: 10000 });
const setupAxiosInterceptors = (getStoredToken, logout) => {
  apiClient.interceptors.request.use((config) => {
    const token = getStoredToken?.();
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
  });
  apiClient.interceptors.response.use(
    (res) => res,
    (err) => {
      if (err.response?.status === 401) logout?.();
      return Promise.reject(err);
    }
  );
};

// ===================== DRAG & DROP =====================
const DraggableField = ({ name, table }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "FIELD",
    item: { name, table },
    collect: (monitor) => ({ isDragging: !!monitor.isDragging() }),
  }));
  return (
    <div
      ref={drag}
      className="p-1 m-1 border bg-white shadow rounded cursor-pointer text-sm"
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      {table}.{name}
    </div>
  );
};

const DropZone = ({ title, fields, onDrop }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "FIELD",
    drop: (item) => onDrop(item),
    collect: (monitor) => ({ isOver: !!monitor.isOver() }),
  }));
  return (
    <div
      ref={drop}
      className={`p-2 border rounded min-h-[60px] mb-4 ${
        isOver ? "bg-green-100" : "bg-gray-50"
      }`}
    >
      <h4 className="font-semibold mb-2">{title}</h4>
      <div className="flex flex-wrap">
        {fields.map((f, i) => (
          <span key={i} className="px-2 py-1 m-1 bg-blue-200 text-xs rounded">
            {f.table}.{f.name}
          </span>
        ))}
      </div>
    </div>
  );
};

// ===================== MODULO PRINCIPAL =====================
const ReportesModule = () => {
  const { user, getStoredToken, logout } = useAuth();

  const [metadata, setMetadata] = useState({});
  const [rows, setRows] = useState([]);
  const [cols, setCols] = useState([]);
  const [metrics, setMetrics] = useState([]);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [tipoReporte, setTipoReporte] = useState("empleados_por_departamento");
  const [sqlQuery, setSqlQuery] = useState("");

  // Configurar Axios al montar
  useEffect(() => setupAxiosInterceptors(getStoredToken, logout), [getStoredToken, logout]);

  // Cargar metadata
  useEffect(() => {
    if (!user) return;
    const loadMetadata = async () => {
      try {
        setLoading(true);
        const res = await apiClient.get("/reportes/metadata");
        setMetadata(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Error cargando metadata");
      } finally {
        setLoading(false);
      }
    };
    loadMetadata();
  }, [user]);

  // ===================== UTILIDADES =====================
  const generarSQL = () => {
    if (!rows.length || !metrics.length) return "";
    const tablas = Array.from(new Set([...rows, ...cols, ...metrics].map((f) => f.table)));
    const select = [...rows, ...cols, ...metrics].map((f) => `${f.table}.${f.name}`);
    return `SELECT ${select.join(", ")} FROM ${tablas.join(", ")} /* relaciones automáticas */`;
  };

  const ejecutarReporte = async () => {
    const query = generarSQL();
    if (!query) return setError("Selecciona al menos una fila y una métrica");
    try {
      setLoading(true);
      const res = await apiClient.post("/reportes/custom", { sqlQuery: query });
      setData(res.data);
    } catch {
      setError("Error al ejecutar reporte dinámico");
    } finally {
      setLoading(false);
    }
  };

  const fetchPredefinido = async () => {
    try {
      setLoading(true);
      const res = await apiClient.get(`/reportes/predefinidos/${tipoReporte}`);
      setData(res.data);
    } catch {
      setError("Error generando reporte predefinido");
    } finally {
      setLoading(false);
    }
  };

  const fetchCustom = async () => {
    if (!sqlQuery.trim()) return;
    try {
      setLoading(true);
      const res = await apiClient.post("/reportes/custom", { sqlQuery });
      setData(res.data);
    } catch {
      setError("Error ejecutando SQL personalizado");
    } finally {
      setLoading(false);
    }
  };

  const exportar = async (formato) => {
    try {
      setLoading(true);
      const res = await apiClient.post("/reportes/export", { formato, data }, { responseType: "blob" });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.download = `reporte.${formato}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch {
      setError("Error exportando el reporte");
    } finally {
      setLoading(false);
    }
  };

  // Evitar duplicados en drag & drop
  const addUnique = (arr, item) => {
    if (!arr.find((f) => f.table === item.table && f.name === item.name)) {
      return [...arr, item];
    }
    return arr;
  };

  const memoMetadata = useMemo(() => Object.entries(metadata), [metadata]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Módulo de Reportes</h2>

        {error && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
            {error} <button onClick={() => setError(null)}>×</button>
          </div>
        )}

        {loading && <div className="mb-4 p-3 bg-blue-100 border border-blue-400 text-blue-700 rounded">Cargando...</div>}

        {/* ----------- Reportes Predefinidos ----------- */}
        <div className="mb-6 flex gap-2 items-center">
          <select value={tipoReporte} onChange={(e) => setTipoReporte(e.target.value)}>
            <option value="empleados_por_departamento">Empleados por departamento</option>
            <option value="vacantes_abiertas">Vacantes abiertas</option>
          </select>
          <button onClick={fetchPredefinido} className="px-4 py-1 bg-blue-500 text-white rounded">
            Generar
          </button>
        </div>

        {/* ----------- SQL Personalizado ----------- */}
        <div className="mb-6">
          <textarea
            rows={3}
            className="border w-full p-2"
            placeholder="Escribe tu consulta SQL..."
            value={sqlQuery}
            onChange={(e) => setSqlQuery(e.target.value)}
          />
          <button onClick={fetchCustom} className="mt-2 px-4 py-1 bg-green-500 text-white rounded">
            Ejecutar SQL
          </button>
        </div>

        {/* ----------- Drag & Drop Constructor ----------- */}
        <div className="mb-6 grid grid-cols-3 gap-4">
          <div className="col-span-1 border p-2 rounded">
            <h3 className="font-semibold mb-2">Campos disponibles</h3>
            {memoMetadata.length === 0
              ? <p className="text-gray-500">Cargando...</p>
              : memoMetadata.map(([table, fields]) => (
                  <div key={table} className="mb-2">
                    <h4 className="font-bold">{table}</h4>
                    {fields.map((field) => (
                      <DraggableField key={field} table={table} name={field} />
                    ))}
                  </div>
                ))}
          </div>

          <div className="col-span-2">
            <DropZone title="Filas" fields={rows} onDrop={(item) => setRows(addUnique(rows, item))} />
            <DropZone title="Columnas" fields={cols} onDrop={(item) => setCols(addUnique(cols, item))} />
            <DropZone title="Métricas" fields={metrics} onDrop={(item) => setMetrics(addUnique(metrics, item))} />

            <button onClick={ejecutarReporte} className="px-4 py-2 bg-blue-600 text-white rounded">
              Generar Reporte
            </button>
          </div>
        </div>

        {/* ----------- Resultados ----------- */}
        {data.length > 0 && (
          <div>
            <div className="mb-4">
              <button onClick={() => exportar("csv")} className="mr-2 px-4 py-1 bg-gray-600 text-white rounded">CSV</button>
              <button onClick={() => exportar("excel")} className="mr-2 px-4 py-1 bg-yellow-600 text-white rounded">Excel</button>
              <button onClick={() => exportar("pdf")} className="px-4 py-1 bg-red-600 text-white rounded">PDF</button>
            </div>

            <table className="table-auto border-collapse border border-gray-400 w-full mb-6">
              <thead>
                <tr>{Object.keys(data[0]).map((k) => <th key={k} className="border px-2 py-1">{k}</th>)}</tr>
              </thead>
              <tbody>{data.map((r, i) => (
                <tr key={i}>{Object.values(r).map((v, j) => <td key={j} className="border px-2 py-1">{v}</td>)}</tr>
              ))}</tbody>
            </table>

            <div style={{ width: "100%", height: 400 }}>
              <ResponsiveContainer>
                <BarChart data={data}>
                  <XAxis dataKey={Object.keys(data[0])[0]} />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey={Object.keys(data[0])[1]} fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        )}
      </div>
    </DndProvider>
  );
};

export default ReportesModule;
