import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { 
  Users, 
  Plus, 
  Search, 
  Filter, 
  Download, 
  Upload, 
  Eye, 
  Edit, 
  Trash2, 
  Mail, 
  Phone, 
  MapPin, 
  Calendar, 
  DollarSign, 
  Building, 
  User, 
  FileText, 
  MoreVertical,
  X,
  Save,
  Camera,
  Star,
  Shield,
  Award,
  TrendingUp,
  Clock,
  CheckCircle,
  AlertCircle
} from 'lucide-react';

const EmpleadosModule = () => {
  // Estados principales
  const [empleados, setEmpleados] = useState([]);
  const [departamentos, setDepartamentos] = useState([]);
  const [puestos, setPuestos] = useState([]);
  const [filtros, setFiltros] = useState({
    busqueda: '',
    departamento: '',
    puesto: '',
    estado: 'todos'
  });
  const [modalActivo, setModalActivo] = useState(null);
  const [empleadoSeleccionado, setEmpleadoSeleccionado] = useState(null);
  const [paginaActual, setPaginaActual] = useState(1);
  const [cargando, setCargando] = useState(true);
  const empleadosPorPagina = 10;

  // Datos de empleados basados en usuarios reales del sistema
  

  const departamentosMock = [
    { id: 1, nombre: 'Recursos Humanos', empleados: 3, jefe: 'Luis MartÃ­nez' },
    { id: 2, nombre: 'TecnologÃ­a', empleados: 2, jefe: 'MarÃ­a GonzÃ¡lez' },
    { id: 3, nombre: 'Operaciones', empleados: 1, jefe: 'Carlos RodrÃ­guez' },
    { id: 4, nombre: 'AdministraciÃ³n', empleados: 1, jefe: 'Sistema Admin' }
  ];

  const puestosMock = [
    'Desarrollador Frontend', 'Gerente de TecnologÃ­a', 'Director de Operaciones', 
    'Especialista en RRHH', 'Director de RRHH', 'Gerente de RRHH',
    'Administrador del Sistema', 'Analista de Sistemas', 'Desarrollador Backend',
    'Especialista en Seguridad', 'Coordinador de Operaciones'
  ];

// InicializaciÃ³n
useEffect(() => {
  const fetchData = async () => {
    try {
      setCargando(true);

      // ðŸ”¹ Obtener el token de localStorage
      const token = localStorage.getItem("rrhh_token");
      if (!token) {
        throw new Error("No hay token en localStorage, inicia sesiÃ³n primero");
      }

      // ðŸ”¹ Llamar API con token en headers
      const response = await fetch("http://localhost:5000/api/empleados/list", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error(`Error HTTP: ${response.status}`);
      }

      const data = await response.json();
      console.log("ðŸ“Œ Respuesta cruda del backend:", data);

      // âœ… Detectar si data es array o viene en { empleados: [...] }
      const empleadosRaw = Array.isArray(data) ? data : data.empleados;

      if (!empleadosRaw || empleadosRaw.length === 0) {
        console.warn("âš  No hay empleados disponibles en la respuesta");
        setEmpleados([]);
        setDepartamentos([]);
        setPuestos([]);
        return;
      }

      console.log("Datos SIN mapear:", empleadosRaw[0]);
      console.log("Fecha original ejemplo:", empleadosRaw[0]?.FECHAINGRESO);

      // ðŸ”¹ Mapear empleados
      const empleadosMapeados = empleadosRaw.map(emp => ({
        id: emp.EmpleadoID,
        codigo: `EMP${String(emp.EmpleadoID).padStart(3, "0")}`,
        nombre: emp.NOMBRE || "",
        apellido: emp.APELLIDO || "",
        email:
          emp.Email ||
          `${(emp.NOMBRE || "empleado").split(" ")[0].toLowerCase()}${(emp.APELLIDO || "empleado")
            .split(" ")[0]
            .toLowerCase()}@prodominicana.gob.do`,
        telefono: emp.Telefono || "",
        direccion: emp.Direccion || "",
        departamento: emp.DEPARTAMENTO_NOMBRE || "Sin departamento",
        puesto: emp.CARGO || "Sin puesto",
        supervisor: "",
        fechaIngreso: emp.FECHAINGRESO ? emp.FECHAINGRESO.split("T")[0] : "",
        fechaIngresoDisplay: emp.FECHAINGRESO
          ? (() => {
              try {
                const fecha = new Date(emp.FECHAINGRESO);
                if (isNaN(fecha.getTime())) return "Fecha no vÃ¡lida";
                return fecha.toLocaleDateString("es-DO");
              } catch (error) {
                return "Fecha no vÃ¡lida";
              }
            })()
          : "Sin fecha",
        salario: emp.Salario || 0,
        estado: emp.ESTADO === true ? "activo" : "inactivo",
        foto: "/api/placeholder/80/80",
        cedula: emp.CEDULA || "",
      }));

      console.log("Empleados mapeados:", empleadosMapeados[0]);
      setEmpleados(empleadosMapeados);

      // ðŸ”¹ EXTRAER departamentos Ãºnicos
      const departamentosUnicos = [
        ...new Set(
          empleadosMapeados
            .map(emp => emp.departamento)
            .filter(dept => dept && dept !== "Sin departamento")
        ),
      ];

      const departamentosReales = departamentosUnicos.map((nombre, index) => ({
        id: index + 1,
        nombre,
      }));

      console.log("Departamentos Ãºnicos encontrados:", departamentosReales.length);
      setDepartamentos(departamentosReales);

      // ðŸ”¹ EXTRAER puestos Ãºnicos
      const puestosUnicos = [
        ...new Set(
          empleadosMapeados
            .map(emp => emp.puesto)
            .filter(puesto => puesto && puesto !== "Sin puesto")
        ),
      ];

      const puestosReales = puestosUnicos.map((nombre, index) => ({
        id: index + 1,
        nombre,
      }));

      console.log("Puestos Ãºnicos encontrados:", puestosReales.length);
      setPuestos(puestosReales);
    } catch (error) {
      console.error("âŒ Error cargando empleados:", error);
      setEmpleados([]);
    } finally {
      setCargando(false);
    }
  };

  fetchData();
}, []);



// ðŸ”¹ BUSCADOR ARREGLADO - Filtrado y bÃºsqueda
const empleadosFiltrados = useMemo(() => {
  return empleados.filter(empleado => {
    if (!empleado) return false;
    
    // BÃºsqueda en mÃºltiples campos
    const terminoBusqueda = filtros.busqueda.toLowerCase().trim();
    const coincideBusqueda = terminoBusqueda === '' || [
      empleado.nombre,
      empleado.apellido, 
      empleado.email,
      empleado.codigo,
      empleado.cedula,
      empleado.puesto,
      empleado.departamento
    ].some(campo => 
      (campo || '').toLowerCase().includes(terminoBusqueda)
    );
    
    // Filtro por departamento
    const coincideDepartamento = 
      filtros.departamento === '' || 
      empleado.departamento === filtros.departamento;
    
    // Filtro por puesto
    const coincidePuesto = 
      filtros.puesto === '' || 
      empleado.puesto === filtros.puesto;
    
    // Filtro por estado
    const coincideEstado = 
      filtros.estado === 'todos' || 
      empleado.estado === filtros.estado;
    
    return coincideBusqueda && coincideDepartamento && coincidePuesto && coincideEstado;
  });
}, [empleados, filtros]);

// ðŸ”¹ ESTADÃSTICAS CORREGIDAS
const estadisticas = useMemo(() => {
  const empleadosActivos = empleados.filter(emp => emp.estado === 'activo').length;
  const departamentosUnicos = new Set(
    empleados
      .map(emp => emp.departamento)
      .filter(dept => dept && dept !== 'Sin departamento')
  ).size;
  
  // Empleados del mes actual
  const fechaActual = new Date();
  const mesActual = fechaActual.getMonth();
  const aÃ±oActual = fechaActual.getFullYear();
  
  const nuevosEsteMes = empleados.filter(emp => {
