import React, { useState, useEffect } from 'react';
import { X, FileText, Upload, AlertCircle } from 'lucide-react';
import FormularioIngreso from './FormularioIngreso';
import FormularioPromocion from './FormularioPromocion';
import FormularioAjusteSalarial from './FormularioAjusteSalarial';
import FormularioCambioDepartamento from './FormularioCambioDepartamento';
import FormularioTerminacion from './FormularioTerminacion';

const API_URL = process.env.REACT_APP_API_URL || 'http://192.168.0.239:5000/api';

const NuevaAccion = ({ onClose, onSuccess }) => {
  const [tiposAccion, setTiposAccion] = useState([]);
  const [tipoSeleccionado, setTipoSeleccionado] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    cargarTiposAccion();
  }, []);

  const cargarTiposAccion = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/acciones-personal/tipos`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();
      if (data.success) {
        setTiposAccion(data.tipos);
      }
    } catch (error) {
      console.error('Error al cargar tipos de acci贸n:', error);
    } finally {
      setLoading(false);
    }
  };

  const agruparPorCategoria = () => {
    const agrupado = {};
    tiposAccion.forEach(tipo => {
      if (!agrupado[tipo.categoria]) {
        agrupado[tipo.categoria] = [];
      }
      agrupado[tipo.categoria].push(tipo);
    });
    return agrupado;
  };

  const renderFormulario = () => {
    switch (tipoSeleccionado) {
      case 'INGRESO':
      case 'REINGRESO':
        return <FormularioIngreso tipo={tipoSeleccionado} onClose={onClose} onSuccess={onSuccess} />;
      
      case 'PROMOCION':
        return <FormularioPromocion onClose={onClose} onSuccess={onSuccess} />;
      
      case 'AJUSTE_SALARIAL':
        return <FormularioAjusteSalarial onClose={onClose} onSuccess={onSuccess} />;
      
      case 'CAMBIO_DEPTO':
        return <FormularioCambioDepartamento onClose={onClose} onSuccess={onSuccess} />;
      
      case 'TERMINACION':
      case 'JUBILACION':
        return <FormularioTerminacion tipo={tipoSeleccionado} onClose={onClose} onSuccess={onSuccess} />;
      
      default:
        return (
          <div style={{ textAlign: 'center', padding: '3rem' }}>
            <FileText size={64} style={{ margin: '0 auto 1rem', color: '#9ca3af' }} />
            <p style={{ color: '#6b7280' }}>Formulario para este tipo de acci贸n en desarrollo</p>
          </div>
        );
    }
  };

  const tiposAgrupados = agruparPorCategoria();

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '1rem'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        width: '100%',
        maxWidth: tipoSeleccionado ? '1000px' : '700px',
        maxHeight: '90vh',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)'
      }}>
        {/* Header */}
        <div style={{
          padding: '1.5rem',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
          color: 'white',
          borderRadius: '12px 12px 0 0'
        }}>
          <div>
            <h2 style={{ margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>
              {tipoSeleccionado ? 'Nueva Acci贸n de Personal' : 'Seleccionar Tipo de Acci贸n'}
            </h2>
            {tipoSeleccionado && (
              <p style={{ margin: '0.5rem 0 0', fontSize: '0.875rem', opacity: 0.9 }}>
                {tiposAccion.find(t => t.codigo === tipoSeleccionado)?.nombre}
              </p>
            )}
          </div>
          <button
            onClick={onClose}
            style={{
              background: 'rgba(255, 255, 255, 0.2)',
              border: 'none',
              borderRadius: '6px',
              padding: '0.5rem',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              color: 'white'
            }}
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '1.5rem'
        }}>
          {!tipoSeleccionado ? (
            loading ? (
              <div style={{ textAlign: 'center', padding: '3rem' }}>
                <div style={{
                  width: '50px',
                  height: '50px',
                  border: '4px solid #e5e7eb',
                  borderTop: '4px solid #3b82f6',
                  borderRadius: '50%',
                  animation: 'spin 1s linear infinite',
                  margin: '0 auto'
                }}></div>
              </div>
            ) : (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
                {Object.entries(tiposAgrupados).map(([categoria, tipos]) => (
                  <div key={categoria}>
                    <h3 style={{
                      fontSize: '0.875rem',
                      fontWeight: '600',
                      color: '#6b7280',
                      textTransform: 'uppercase',
                      marginBottom: '0.75rem',
                      letterSpacing: '0.05em'
                    }}>
                      {categoria}
                    </h3>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
                      gap: '1rem'
                    }}>
                      {tipos.map(tipo => (
                        <button
                          key={tipo.codigo}
                          onClick={() => setTipoSeleccionado(tipo.codigo)}
                          style={{
                            padding: '1rem',
                            background: 'white',
                            border: '2px solid #e5e7eb',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            textAlign: 'left',
                            transition: 'all 0.2s',
                            fontSize: '0.875rem',
                            fontWeight: '500',
                            color: '#374151'
                          }}
                          onMouseOver={(e) => {
                            e.currentTarget.style.borderColor = '#3b82f6';
                            e.currentTarget.style.background = '#eff6ff';
                            e.currentTarget.style.transform = 'translateY(-2px)';
                          }}
                          onMouseOut={(e) => {
                            e.currentTarget.style.borderColor = '#e5e7eb';
                            e.currentTarget.style.background = 'white';
                            e.currentTarget.style.transform = 'translateY(0)';
                          }}
                        >
                          {tipo.nombre}
                        </button>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )
          ) : (
            <div>
              {renderFormulario()}
            </div>
          )}
        </div>

        {/* Footer (solo si no hay tipo seleccionado) */}
        {!tipoSeleccionado && (
          <div style={{
            padding: '1.5rem',
            borderTop: '1px solid #e5e7eb',
            display: 'flex',
            justifyContent: 'flex-end'
          }}>
            <button
              onClick={onClose}
              style={{
                padding: '0.625rem 1.5rem',
                background: '#f3f4f6',
                color: '#374151',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                fontSize: '0.875rem',
                fontWeight: '600',
                cursor: 'pointer'
              }}
            >
              Cancelar
            </button>
          </div>
        )}

        <style>{`
          @keyframes spin {
            to { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    </div>
  );
};

export default NuevaAccion;
