import React, { useState, useEffect } from 'react';
import { getNominas, procesarNomina, getNominaById, marcarNominaPagada, getEmpleadosActivos } from '../../../api/nominaService';
import { 
  DollarSign, Download, Calendar, FileText, Eye, Calculator, CreditCard,
  PieChart, TrendingUp, Settings, AlertCircle, CheckCircle, Clock, User,
  Building, Users, Edit, Trash2, Plus, Search, Filter, Mail, Upload, Save,
  X, UserPlus, Briefcase, MapPin, Phone, FileBarChart, Banknote, Receipt,
  Send, Award, Gift, Zap, FileCheck, BarChart3, Target, ShieldCheck, ArrowRight
} from 'lucide-react';

const SistemaNominaCompleto = () => {
  // Estados principales
  const [activeTab, setActiveTab] = useState('empleados');
  const [nominasProcesadas, setNominasProcesadas] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  console.log('üìã Estado actual empleados:', empleados?.length || 0, empleados);
  console.log('üîÑ Loading:', loading);
  console.log('‚ùå Error:', error);

 // Funci√≥n para cargar empleados
const cargarEmpleados = async () => {
 try {
   console.log('üöÄ Iniciando cargarEmpleados...');
   setLoading(true);
   setError(null);
   const data = await getEmpleadosActivos();
   console.log('‚úÖ Empleados recibidos:', data);
   console.log('üìä Cantidad de empleados:', data?.length || 0);
   setEmpleados(data);
   console.log('üéØ Estado empleados despu√©s de setEmpleados:', data);
 } catch (error) {
   console.error('‚ùå Error al cargar empleados:', error);
   setError('Error al cargar empleados desde la base de datos');
 } finally {
   setLoading(false);
 }
};

// useEffect para cargar cuando cambie de tab
useEffect(() => {
 console.log('üîÑ Tab cambi√≥ a:', activeTab);
 console.log('üîÑ Tipo de activeTab:', typeof activeTab);
 
 if (activeTab === 'empleados') {
   console.log('‚úÖ Ejecutando cargarEmpleados...');
   cargarEmpleados();
 } else {
   console.log('‚ùå activeTab no es "empleados", es:', activeTab);
 }
}, [activeTab]);
  
  
  
  // resto de tu c√≥digo...

// Modal de incidencias
const ModalIncidencia = ({ empleado, onClose, onSave, empleados, configNomina }) => {
  const [formData, setFormData] = useState({
    empleadoId: empleado?.id || '',
    tipo: 'horas_extra',
    fecha: new Date().toISOString().split('T')[0],
    fechaInicio: '',
    fechaFin: '',
    horas: 0,
    tipoRecargo: 'extra_normal',
    diasSolicitados: 0,
    conGoce: true,
    justificada: true,
    motivo: '',
    descripcion: '',
    aprobado: false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0,0,0,0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '1rem'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '0.75rem',
        boxShadow: '0 20px 25px rgba(0,0,0,0.1)',
        maxWidth: '600px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'auto'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '1rem',
          borderBottom: '1px solid #e5e7eb'
        }}>
          <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#111827' }}>
            üìã Nueva Incidencia
          </h3>
          <button
            onClick={onClose}
            style={{
              padding: '0.25rem',
              background: '#f3f4f6',
              border: 'none',
              borderRadius: '0.25rem',
              cursor: 'pointer'
            }}
          >
            <X style={{ width: '1rem', height: '1rem' }} />
          </button>
        </div>

        <form onSubmit={handleSubmit} style={{ padding: '1.5rem' }}>
          <div style={{ display: 'grid', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.5rem' }}>
                Empleado *
              </label>
              <select
                value={formData.empleadoId}
                onChange={(e) => setFormData(prev => ({ ...prev, empleadoId: Number(e.target.value) }))}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.375rem',
                  fontSize: '0.875rem'
                }}
                required
              >
                <option value="">Seleccionar empleado</option>
                {empleados.filter(e => e.estado === 'activo').map(emp => (
                  <option key={emp.id} value={emp.id}>
                    {emp.nombre} {emp.apellidos} - {emp.cargo}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.5rem' }}>
                Tipo de Incidencia *
              </label>
              <select
                value={formData.tipo}
                onChange={(e) => setFormData(prev => ({ ...prev, tipo: e.target.value }))}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '0.375rem',
                  fontSize: '0.875rem'
                }}
                required
              >
                <option value="horas_extra">Horas Extra</option>
                <option value="falta">Falta</option>
                <option value="permiso">Permiso</option>
                <option value="vacaciones">Vacaciones</option>
                <option value="licencia">Licencia M√©dica</option>
              </select>
            </div>

            {formData.tipo === 'horas_extra' && (
              <>
                <div>
                  <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.5rem' }}>
                    Fecha *
                  </label>
                  <input
                    type="date"
                    value={formData.fecha}
                    onChange={(e) => setFormData(prev => ({ ...prev, fecha: e.target.value }))}
                    style={{
                      width: '100%',
                      padding: '0.5rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '0.375rem',
                      fontSize: '0.875rem'
                    }}
                    required
                  />
                </div>
                <div>
                  <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.5rem' }}>
                    Horas Trabajadas *
                  </label>
                  <input
                    type="number"
                    value={formData.horas}
                    onChange={(e) => setFormData(prev => ({ ...prev, horas: Number(e.target.value) }))}
                    min="0"
                    max="12"
                    step="0.5"
                    style={{
                      width: '100%',
                      padding: '0.5rem',
                      border: '1px solid #d1d5db',
