                      <span style={{ fontSize: '0.875rem' }}>
                        {empleado.nombre} {empleado.apellidos}
                      </span>
                      <span style={{ fontSize: '0.875rem', fontWeight: '500', color: '#dc2626' }}>
                        {formatearMoneda(empleado.salarioBase)}
                      </span>
                    </div>
