// src/context/AuthContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react'
import axios from 'axios'

const AuthContext = createContext()

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null)
  const [permisos, setPermisos] = useState([])
  const [loading, setLoading] = useState(true)

  const API_URL = process.env.REACT_APP_API_URL || 'http://192.168.0.239:5000/api'

  // ============================
  // FUNCIÓN PARA OBTENER TOKEN
  // ============================
  const getStoredToken = () => {
    const token = localStorage.getItem('rrhh_token')
    console.log('getStoredToken llamado, token:', token ? 'presente' : 'ausente')
    return token
  }

  // Configurar axios con interceptores para manejo automático de tokens
  useEffect(() => {
    // Interceptor para agregar token a todas las peticiones
    const requestInterceptor = axios.interceptors.request.use(
      (config) => {
        const token = getStoredToken()
        if (token) {
          config.headers.Authorization = `Bearer ${token}`
        }
        console.log('Petición enviada con token:', token ? 'SI' : 'NO')
        return config
      },
      (error) => Promise.reject(error)
    )

    // Interceptor para manejar errores 401
    const responseInterceptor = axios.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          console.warn('Error 401: Token inválido o expirado')
          logout()
        }
        return Promise.reject(error)
      }
    )

    // Limpiar interceptores al desmontar
    return () => {
      axios.interceptors.request.eject(requestInterceptor)
      axios.interceptors.response.eject(responseInterceptor)
    }
  }, [])

  // Validación de token mejorada
  const isTokenValid = (token) => {
    if (!token) {
      console.log('No hay token para validar')
      return false
    }

    try {
      // Verificar formato básico del token
      if (token.length < 10) {
        console.log('Token demasiado corto')
        return false
      }

      // Si es JWT, validar estructura
      if (token.includes('.')) {
        const parts = token.split('.')
        if (parts.length !== 3) {
          console.log('JWT mal formado')
          return false
        }

        const payload = JSON.parse(atob(parts[1]))
        console.log('Payload del token:', payload)

        // Si no tiene exp, considerar válido (tokens sin expiración)
        if (!payload.exp) {
          console.log('Token sin expiración - considerado válido')
          return true
        }

        const currentTime = Date.now() / 1000
        const isValid = payload.exp > currentTime
        console.log('Token expirado:', !isValid)
        return isValid
      } else {
        // Token simple (no JWT)
        console.log('Token simple - considerado válido')
        return true
      }
    } catch (error) {
      console.error('Error validando token:', error)
      return false
    }
  }

  const mapEstado = (estado) => (estado === 1 || estado === true ? 'Activo' : 'Inactivo')

  const formatUser = (userData) => ({
    id: userData.id,
    name: `${userData.nombre || ''} ${userData.apellido || ''}`.trim() || 'Usuario',
    position: userData.puesto || userData.rol || '',
    role: userData.rol || 'Sistema',
    email: userData.email,
    empleadoId: userData.empleadoId,
    estado: mapEstado(userData.estado),
  })

  // ============================
  // LOGIN
  // ============================
  const login = async (email, password) => {
    setLoading(true)
    console.log('Intentando login para:', email)

    try {
      const res = await fetch(`${API_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      })

      const data = await res.json()
      console.log('Respuesta del login:', data)

      if (!res.ok) {
        throw new Error(data.message || 'Error al iniciar sesión')
      }

      // Guardar token
      localStorage.setItem('rrhh_token', data.token)
      console.log('Token guardado:', data.token.substring(0, 20) + '...')

      // Establecer usuario y permisos
      const formattedUser = formatUser(data.user)
      setUser(formattedUser)
      setPermisos(data.permisos || [])

      console.log('Usuario logueado:', formattedUser)
      console.log('Permisos:', data.permisos)

      return { success: true }
    } catch (error) {
      console.error('Error en login:', error)
      return { success: false, error: error.message }
    } finally {
      setLoading(false)
    }
  }

  // ============================
  // LOGOUT
  // ============================
  const logout = () => {
    console.log('Haciendo logout...')
    localStorage.removeItem('rrhh_token')
    setUser(null)
    setPermisos([])
    console.log('Logout completado')
  }

  // ============================
  // VERIFICAR TOKEN / USUARIO
  // ============================
  const verifyUser = async () => {
    console.log('Verificando usuario...')
    const token = getStoredToken()
    
    if (!token) {
      console.log('No hay token guardado')
      setLoading(false)
      return
    }

    if (!isTokenValid(token)) {
      console.log('Token inválido, haciendo logout')
      logout()
      setLoading(false)
      return
    }

    try {
      console.log('Verificando token con servidor...')
      const res = await fetch(`${API_URL}/auth/verify`, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      })

      const data = await res.json()
      console.log('Respuesta de verificación:', data)

      // Chequeo para usuario inactivo o token inválido
      if (!res.ok || data.success === false) {
        console.warn('Token inválido o usuario inactivo, haciendo logout')
        logout()
        return
      }

      // Verificar si el usuario está activo
      if (data.user.estado === 1 || data.user.estado === true) {
        const formattedUser = formatUser(data.user)
        setUser(formattedUser)
        setPermisos(data.permisos || [])
        console.log('Usuario verificado y activo:', formattedUser)
      } else {
        console.warn('Usuario inactivo, haciendo logout')
        logout()
      }
    } catch (error) {
      console.error('Error verificando usuario:', error)
      logout()
    } finally {
      setLoading(false)
    }
  }

  // Mantener sesión al cargar la app
  useEffect(() => {
    verifyUser()
  }, [])

  // ============================
  // FORGOT PASSWORD
  // ============================
  const forgotPassword = async (email) => {
    try {
      console.log('Solicitando reset de contraseña para:', email)
      const res = await fetch(`${API_URL}/auth/forgot-password`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      })

      const data = await res.json()
      if (!res.ok) throw new Error(data.message || 'Error al enviar email')
      
      console.log('Email de reset enviado')
      return { success: true, message: data.message || 'Email enviado correctamente' }
    } catch (error) {
      console.error('Error en forgot password:', error)
      return { success: false, error: error.message }
    }
  }

  // ============================
  // RESET PASSWORD
  // ============================
  const resetPassword = async (token, newPassword) => {
    try {
      console.log('Reseteando contraseña con token')
      const res = await fetch(`${API_URL}/auth/reset-password`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token, newPassword }),
      })

      const data = await res.json()
      if (!res.ok) throw new Error(data.message || 'Error al restablecer contraseña')
      
      console.log('Contraseña restablecida exitosamente')
      return { success: true, message: data.message || 'Contraseña actualizada correctamente' }
    } catch (error) {
      console.error('Error en reset password:', error)
      return { success: false, error: error.message }
    }
  }

  // ============================
  // CHANGE PASSWORD (usuario logueado)
  // ============================
  const changePassword = async (currentPassword, newPassword) => {
    const token = getStoredToken()
    if (!token || !isTokenValid(token)) {
      return { success: false, error: 'No estás autenticado' }
    }

    try {
      console.log('Cambiando contraseña de usuario logueado')
      const res = await fetch(`${API_URL}/auth/change-password`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      })

      const data = await res.json()
      if (!res.ok) {
        if (res.status === 401) logout()
        throw new Error(data.message || 'Error cambiando contraseña')
      }

      console.log('Contraseña cambiada exitosamente')
      return { success: true, message: data.message || 'Contraseña cambiada correctamente' }
    } catch (error) {
      console.error('Error cambiando contraseña:', error)
      return { success: false, error: error.message }
    }
  }

  // ============================
  // FUNCIONES DE DEBUG
  // ============================
  const getTokenInfo = () => {
    const token = getStoredToken()
    if (!token) return { hasToken: false }

    const info = {
      hasToken: true,
      tokenLength: token.length,
      tokenPreview: token.substring(0, 20) + '...',
      isValid: isTokenValid(token)
    }

    if (token.includes('.')) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]))
        info.payload = payload
        info.isJWT = true
      } catch (e) {
        info.isJWT = false
        info.parseError = e.message
      }
    } else {
      info.isJWT = false
      info.tokenType = 'simple'
    }

    return info
  }

  // Crear un token temporal para desarrollo/testing
  const setTempToken = (tokenValue = 'temp-dev-token-12345') => {
    console.log('Estableciendo token temporal:', tokenValue)
    localStorage.setItem('rrhh_token', tokenValue)
    
    // Establecer usuario temporal
    const tempUser = {
      id: 1,
      nombre: 'Usuario',
      apellido: 'Temporal',
      email: 'temp@test.com',
      rol: 'Admin',
      estado: 1
    }
    
    setUser(formatUser(tempUser))
    setPermisos(['admin', 'reportes', 'usuarios'])
    console.log('Usuario temporal establecido')
  }

  return (
    <AuthContext.Provider
      value={{
        user,
        permisos,
        login,
        logout,
        getStoredToken, // <- FUNCIÓN AGREGADA
        forgotPassword,
        resetPassword,
        changePassword,
        loading,
        isTokenValid,
        // Funciones de debug
        getTokenInfo,
        setTempToken,
        verifyUser
      }}
    >
      {children}
    </AuthContext.Provider>
  )
}

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) throw new Error('useAuth debe usarse dentro de un AuthProvider')
  return context
}
