import React, { useState, useEffect, useMemo } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer, LineChart, Line, PieChart, Pie, Cell } from "recharts";
import { Download, Play, Save, Plus, Edit, Trash2, Database, DragDropIcon, Filter, Eye, FileText, BarChart3 } from "lucide-react";
import { GripVertical } from "lucide-react";

// Simulación del contexto de autenticación
const useAuth = () => ({
  user: { id: 1, name: "Usuario Demo" },
  getStoredToken: () => "demo-token-12345",
  logout: () => console.log("Logout simulado")
});

// Datos de ejemplo para la demostración
const mockMetadata = {
  empleados: ["EmpleadoID", "Nombre", "Apellido", "Email", "Puesto", "Salario", "FechaIngreso", "DepartamentoID"],
  departamentos: ["DepartamentoID", "Nombre", "Descripcion", "GerenteID"],
  proyectos: ["ProyectoID", "Nombre", "FechaInicio", "FechaFin", "Presupuesto", "Estado"],
  ventas: ["VentaID", "ProductoID", "Cantidad", "Precio", "FechaVenta", "ClienteID"]
};

const mockReportesGuardados = [
  { 
    id: 1, 
    nombre: "Empleados por Departamento", 
    origen: "empleados", 
    fechaCreacion: "2024-01-15", 
    fechaModificacion: "2024-01-20", 
    modificadoPor: "Admin",
    descripcion: "Reporte de distribución de empleados por departamento"
  },
  { 
    id: 2, 
    nombre: "Ventas Mensuales", 
    origen: "ventas", 
    fechaCreacion: "2024-01-10", 
    fechaModificacion: "2024-01-25", 
    modificadoPor: "Usuario1",
    descripcion: "Análisis de ventas por mes"
  }
];

// Componente para elementos arrastrables
const DraggableField = ({ name, table, type = "text" }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "FIELD",
    item: { name, table, type },
    collect: (monitor) => ({ isDragging: !!monitor.isDragging() }),
  }));

  const getTypeIcon = (type) => {
    if (type?.toLowerCase().includes('id') || type?.toLowerCase().includes('int')) return '🔢';
    if (type?.toLowerCase().includes('date') || type?.toLowerCase().includes('time')) return '📅';
    if (type?.toLowerCase().includes('decimal') || type?.toLowerCase().includes('float')) return '💰';
    return '📝';
  };

  return (
    <div
      ref={drag}
      className={`flex items-center gap-2 p-2 m-1 border rounded-lg shadow-sm cursor-move bg-white hover:bg-gray-50 transition-all ${
        isDragging ? "opacity-50 scale-95" : "opacity-100"
      }`}
      title={`${table}.${name} (${type || 'text'})`}
    >
      <span className="text-sm">{getTypeIcon(type)}</span>
      <span className="text-xs text-gray-600 font-mono">{table}.</span>
      <span className="text-sm font-medium">{name}</span>
    </div>
  );
};

// Zona de drop para filas, columnas y métricas
const DropZone = ({ title, fields, onDrop, onRemove, icon, color = "blue" }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "FIELD",
    drop: (item) => onDrop(item),
    collect: (monitor) => ({ isOver: !!monitor.isOver() }),
  }));

  const colorClasses = {
    blue: "border-blue-300 bg-blue-50",
    green: "border-green-300 bg-green-50",
    purple: "border-purple-300 bg-purple-50"
  };

  return (
    <div
      ref={drop}
      className={`min-h-[100px] p-3 rounded-lg border-2 border-dashed transition-all ${
        isOver ? `${colorClasses[color]} border-${color}-500` : "border-gray-300 bg-gray-50"
      }`}
    >
      <div className="flex items-center gap-2 mb-3">
        {icon}
        <h4 className="font-semibold text-gray-700">{title}</h4>
        <span className="text-xs text-gray-500">({fields.length})</span>
      </div>
      
      <div className="flex flex-wrap gap-2">
        {fields.length === 0 ? (
          <p className="text-sm text-gray-400 italic">Arrastra campos aquí</p>
        ) : (
          fields.map((field, index) => (
            <div
              key={`${field.table}-${field.name}-${index}`}
              className={`flex items-center gap-2 px-3 py-1 bg-${color}-100 border border-${color}-200 rounded-full text-sm`}
            >
              <span className="font-mono text-xs text-gray-600">{field.table}.</span>
              <span>{field.name}</span>
              <button
                onClick={() => onRemove(index)}
                className="text-red-500 hover:text-red-700 ml-1"
                title="Eliminar campo"
              >
                ×
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Modal para nuevo reporte
const NuevoReporteModal = ({ show, onClose, onSave, metadata }) => {
  const [formData, setFormData] = useState({
    nombre: "",
    origen: "",
    descripcion: "",
    predeterminado: false,
    eliminarDuplicados: false
  });

  const handleSave = () => {
    if (!formData.nombre.trim() || !formData.origen) {
      alert("Nombre y origen son requeridos");
      return;
    }
    onSave(formData);
    setFormData({
      nombre: "",
      origen: "",
      descripcion: "",
      predeterminado: false,
      eliminarDuplicados: false
    });
  };

  if (!show) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-xl w-96 max-w-90vw">
        <div className="flex items-center gap-2 mb-4">
          <Plus className="w-5 h-5 text-green-600" />
          <h3 className="text-lg font-bold">Nuevo Reporte</h3>
        </div>
        
        <div className="space-y-4">
          <div>
            <label className="block font-medium mb-1">Nombre del reporte *</label>
            <input
              type="text"
              value={formData.nombre}
              onChange={(e) => setFormData({...formData, nombre: e.target.value})}
              className="w-full border border-gray-300 p-2 rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Ej: Ventas por región"
            />
          </div>
          
          <div>
            <label className="block font-medium mb-1">Origen del informe *</label>
            <select
              value={formData.origen}
              onChange={(e) => setFormData({...formData, origen: e.target.value})}
              className="w-full border border-gray-300 p-2 rounded focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Seleccionar fuente de datos</option>
              {Object.keys(metadata).map(table => (
                <option key={table} value={table}>{table}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block font-medium mb-1">Descripción</label>
            <textarea
              value={formData.descripcion}
              onChange={(e) => setFormData({...formData, descripcion: e.target.value})}
              className="w-full border border-gray-300 p-2 rounded focus:ring-2 focus:ring-blue-500"
              rows={3}
              placeholder="Describe brevemente este reporte..."
            />
          </div>
          
          <div className="space-y-2">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={formData.predeterminado}
                onChange={() => setFormData({...formData, predeterminado: !formData.predeterminado})}
                className="w-4 h-4 text-blue-600"
              />
              <span className="text-sm">Incluir configuración predeterminada</span>
            </label>
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={formData.eliminarDuplicados}
                onChange={() => setFormData({...formData, eliminarDuplicados: !formData.eliminarDuplicados})}
                className="w-4 h-4 text-blue-600"
              />
              <span className="text-sm">Eliminar filas duplicadas</span>
            </label>
          </div>
        </div>
        
        <div className="flex justify-end gap-2 mt-6">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400 transition-colors"
          >
            Cancelar
          </button>
          <button
            onClick={handleSave}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors flex items-center gap-2"
          >
            <Save className="w-4 h-4" />
            Crear Reporte
          </button>
        </div>
      </div>
    </div>
  );
};

// Componente principal
const ReportesModule = () => {
  const { user } = useAuth();
  const [metadata, setMetadata] = useState(mockMetadata);
  const [reportesGuardados, setReportesGuardados] = useState(mockReportesGuardados);
  const [showNuevoModal, setShowNuevoModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // Estado del constructor de reportes
  const [rows, setRows] = useState([]);
  const [columns, setColumns] = useState([]);
  const [metrics, setMetrics] = useState([]);
  const [sqlQuery, setSqlQuery] = useState("");
  const [customSql, setCustomSql] = useState("");
  const [data, setData] = useState([]);
  const [chartType, setChartType] = useState("bar");

  // Generar SQL automáticamente
  const generateSQL = useMemo(() => {
    if (rows.length === 0 && columns.length === 0 && metrics.length === 0) return "";
    
    const allFields = [...rows, ...columns, ...metrics];
    const tables = [...new Set(allFields.map(f => f.table))];
    const selectFields = allFields.map(f => `${f.table}.${f.name}`).join(", ");
    
    let query = `SELECT ${selectFields}\nFROM ${tables.join(", ")}`;
    
    if (rows.length > 0 || columns.length > 0) {
      const groupBy = [...rows, ...columns].map(f => `${f.table}.${f.name}`).join(", ");
      query += `\nGROUP BY ${groupBy}`;
    }
    
    query += "\nORDER BY " + (metrics.length > 0 ? `${metrics[0].table}.${metrics[0].name} DESC` : "1");
    
    return query;
  }, [rows, columns, metrics]);

  useEffect(() => {
    setSqlQuery(generateSQL);
  }, [generateSQL]);

  // Funciones para manejar drag & drop
  const addField = (targetArray, setter) => (field) => {
    const exists = targetArray.some(f => f.table === field.table && f.name === field.name);
    if (!exists) {
      setter([...targetArray, field]);
    }
  };

  const removeField = (targetArray, setter) => (index) => {
    setter(targetArray.filter((_, i) => i !== index));
  };

  // Ejecutar reporte
  const ejecutarReporte = async () => {
    const query = customSql || sqlQuery;
    if (!query.trim()) {
      setError("No hay consulta para ejecutar");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Simulación de datos (en producción esto vendría del backend)
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const mockData = [
        { departamento: "Ventas", empleados: 25, promedio_salario: 45000 },
        { departamento: "Marketing", empleados: 18, promedio_salario: 42000 },
        { departamento: "IT", empleados: 32, promedio_salario: 55000 },
        { departamento: "RRHH", empleados: 12, promedio_salario: 48000 },
      ];
      
      setData(mockData);
    } catch (err) {
      setError("Error al ejecutar el reporte: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  // Guardar reporte
  const guardarReporte = (nuevoReporte) => {
    const reporte = {
      ...nuevoReporte,
      id: Date.now(),
      fechaCreacion: new Date().toISOString().split('T')[0],
      fechaModificacion: new Date().toISOString().split('T')[0],
      modificadoPor: user.name,
      configuracion: {
        rows,
        columns,
        metrics,
        sqlQuery: customSql || sqlQuery
      }
    };
    
    setReportesGuardados([...reportesGuardados, reporte]);
    setShowNuevoModal(false);
    alert("Reporte guardado exitosamente");
  };

  // Exportar datos
  const exportarDatos = (formato) => {
    if (data.length === 0) {
      alert("No hay datos para exportar");
      return;
    }

    if (formato === 'csv') {
      const csv = [
        Object.keys(data[0]).join(','),
        ...data.map(row => Object.values(row).join(','))
      ].join('\n');
      
      const blob = new Blob([csv], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'reporte.csv';
      a.click();
      URL.revokeObjectURL(url);
    } else if (formato === 'json') {
      const json = JSON.stringify(data, null, 2);
      const blob = new Blob([json], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'reporte.json';
      a.click();
      URL.revokeObjectURL(url);
    }
  };

  // Renderizar gráfico
  const renderChart = () => {
    if (data.length === 0) return null;

    const chartProps = {
      width: "100%",
      height: 300,
      data: data.slice(0, 10) // Limitar a 10 elementos para mejor visualización
    };

    switch (chartType) {
      case "line":
        return (
          <LineChart {...chartProps}>
            <XAxis dataKey={Object.keys(data[0])[0]} />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey={Object.keys(data[0])[1]} stroke="#8884d8" />
          </LineChart>
        );
      case "pie":
        return (
          <PieChart {...chartProps}>
            <Pie
              dataKey={Object.keys(data[0])[1]}
              nameKey={Object.keys(data[0])[0]}
              cx="50%"
              cy="50%"
              fill="#8884d8"
              label
            />
            <Tooltip />
          </PieChart>
        );
      default:
        return (
          <BarChart {...chartProps}>
            <XAxis dataKey={Object.keys(data[0])[0]} />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey={Object.keys(data[0])[1]} fill="#8884d8" />
          </BarChart>
        );
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          {/* Header */}
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <div className="flex justify-between items-center">
              <div>
                <h1 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
                  <BarChart3 className="w-7 h-7 text-blue-600" />
                  Generador de Reportes
                </h1>
                <p className="text-gray-600 mt-1">Crea y gestiona reportes personalizados con drag & drop</p>
              </div>
              <button
                onClick={() => setShowNuevoModal(true)}
                className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors flex items-center gap-2"
              >
                <Plus className="w-4 h-4" />
                Nuevo Reporte
              </button>
            </div>
          </div>

          {/* Mensajes de estado */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6 flex items-center justify-between">
              <span className="text-red-700">{error}</span>
              <button onClick={() => setError(null)} className="text-red-500 hover:text-red-700">×</button>
            </div>
          )}

          {loading && (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
              <div className="flex items-center gap-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
                <span className="text-blue-700">Procesando...</span>
              </div>
            </div>
          )}

          {/* Reportes guardados */}
          <div className="bg-white rounded-lg shadow-sm mb-6">
            <div className="p-6 border-b">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <FileText className="w-5 h-5 text-gray-600" />
                Reportes Guardados ({reportesGuardados.length})
              </h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Nombre</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Origen</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Descripción</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Creado</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Modificado</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Por</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Acciones</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {reportesGuardados.map((reporte) => (
                    <tr key={reporte.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 font-medium text-gray-900">{reporte.nombre}</td>
                      <td className="px-6 py-4 text-sm text-gray-600">{reporte.origen}</td>
                      <td className="px-6 py-4 text-sm text-gray-600">{reporte.descripcion || "-"}</td>
                      <td className="px-6 py-4 text-sm text-gray-600">{reporte.fechaCreacion}</td>
                      <td className="px-6 py-4 text-sm text-gray-600">{reporte.fechaModificacion}</td>
                      <td className="px-6 py-4 text-sm text-gray-600">{reporte.modificadoPor}</td>
                      <td className="px-6 py-4 text-sm">
                        <div className="flex gap-1">
                          <button className="p-1 text-blue-600 hover:bg-blue-50 rounded" title="Ver">
                            <Eye className="w-4 h-4" />
                          </button>
                          <button className="p-1 text-green-600 hover:bg-green-50 rounded" title="Editar">
                            <Edit className="w-4 h-4" />
                          </button>
                          <button className="p-1 text-red-600 hover:bg-red-50 rounded" title="Eliminar">
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Constructor de reportes */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
            {/* SQL Editor */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="font-semibold mb-4 flex items-center gap-2">
                <Database className="w-5 h-5 text-gray-600" />
                Editor SQL
              </h3>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">SQL Generado automáticamente:</label>
                <textarea
                  value={sqlQuery}
                  readOnly
                  className="w-full h-32 p-3 border rounded bg-gray-50 font-mono text-sm resize-none"
                  placeholder="El SQL se genera automáticamente..."
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">SQL Personalizado:</label>
                <textarea
                  value={customSql}
                  onChange={(e) => setCustomSql(e.target.value)}
                  className="w-full h-32 p-3 border rounded font-mono text-sm"
                  placeholder="Escribe tu SQL personalizado aquí..."
                />
              </div>
              <button
                onClick={ejecutarReporte}
                disabled={loading}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 disabled:opacity-50 flex items-center justify-center gap-2"
              >
                <Play className="w-4 h-4" />
                Ejecutar Reporte
              </button>
            </div>

            {/* Constructor Visual */}
            <div className="lg:col-span-2 space-y-6">
              {/* Campos disponibles */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="font-semibold mb-4 flex items-center gap-2">
                  <DragDropIcon className="w-5 h-5 text-gray-600" />
                  Campos Disponibles
                </h3>
                <div className="max-h-48 overflow-y-auto">
                  {Object.entries(metadata).map(([table, fields]) => (
                    <div key={table} className="mb-4">
                      <h4 className="font-medium text-gray-700 mb-2 flex items-center gap-2">
                        <Database className="w-4 h-4" />
                        {table}
                      </h4>
                      <div className="grid grid-cols-2 gap-1">
                        {fields.map((field) => (
                          <DraggableField key={field} table={table} name={field} type="text" />
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Drop Zones */}
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="font-semibold mb-4">Constructor Visual</h3>
                <div className="space-y-4">
                  <DropZone
                    title="Filas"
                    fields={rows}
                    onDrop={addField(rows, setRows)}
                    onRemove={removeField(rows, setRows)}
                    icon={<Filter className="w-4 h-4 text-blue-600" />}
                    color="blue"
                  />
                  <DropZone
                    title="Columnas"
                    fields={columns}
                    onDrop={addField(columns, setColumns)}
                    onRemove={removeField(columns, setColumns)}
                    icon={<BarChart3 className="w-4 h-4 text-green-600" />}
                    color="green"
                  />
                  <DropZone
                    title="Métricas"
                    fields={metrics}
                    onDrop={addField(metrics, setMetrics)}
                    onRemove={removeField(metrics, setMetrics)}
                    icon={<Database className="w-4 h-4 text-purple-600" />}
                    color="purple"
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Resultados */}
          {data.length > 0 && (
            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-lg font-semibold">Resultados ({data.length} registros)</h3>
                <div className="flex gap-2">
                  <select
                    value={chartType}
                    onChange={(e) => setChartType(e.target.value)}
                    className="border rounded px-3 py-1 text-sm"
                  >
                    <option value="bar">Barras</option>
                    <option value="line">Líneas</option>
                    <option value="pie">Circular</option>
                  </select>
                  <button
                    onClick={() => exportarDatos('csv')}
                    className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700 flex items-center gap-1"
                  >
                    <Download className="w-3 h-3" />
                    CSV
                  </button>
                  <button
                    onClick={() => exportarDatos('json')}
                    className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700 flex items-center gap-1"
                  >
                    <Download className="w-3 h-3" />
                    JSON
                  </button>
                </div>
              </div>

              {/* Gráfico */}
              <div className="mb-6 h-80">
                <ResponsiveContainer width="100%" height="100%">
                  {renderChart()}
                </ResponsiveContainer>
              </div>

              {/* Tabla de datos */}
              <div className="overflow-x-auto">
                <table className="w-full border-collapse border border-gray-300">
                  <thead className="bg-gray-50">
                    <tr>
                      {Object.keys(data[0]).map((key) => (
                        <th key={key} className="border border-gray-300 px-4 py-2 text-left font-medium">
                          {key}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {data.slice(0, 50).map((row, index) => (
                      <tr key={index} className="hover:bg-gray-50">
                        {Object.values(row).map((value, cellIndex) => (
                          <td key={cellIndex} className="border border-gray-300 px-4 py-2">
                            {value}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
                {data.length > 50 && (
                  <p className="text-sm text-gray-500 mt-2 text-center">
                    Mostrando primeros 50 de {data.length} registros
                  </p>
                )}
              </div>
            </div>
          )}
        </div>

        {/* Modal */}
        <NuevoReporteModal
          show={showNuevoModal}
          onClose={() => setShowNuevoModal(false)}
          onSave={guardarReporte}
          metadata={metadata}
        />
      </div>
    </DndProvider>
  );
};

export default ReportesModule;
