import React, { useState, useEffect } from 'react';
import { Play, Download, Save, TrendingUp, Users, Building, FileText, DollarSign } from 'lucide-react';

const ReportesVisualModule = () => {
  const [tipoReporte, setTipoReporte] = useState('');
  const [configuracion, setConfiguracion] = useState({});
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [metricas, setMetricas] = useState(null);

  // Cargar mÃ©tricas al inicio
  useEffect(() => {
    cargarMetricas();
  }, []);

  const cargarMetricas = async () => {
    try {
      const response = await fetch(`${API_URL}/reportes/metricas`, {
        headers: { Authorization: `Bearer ${getStoredToken()}` }
      });
      const result = await response.json();
      if (result.success) {
        setMetricas(result.data);
      }
    } catch (error) {
      console.error('Error cargando mÃ©tricas:', error);
    }
  };

  const TIPOS_REPORTES = [
    {
      id: 'empleados',
      nombre: 'Reporte de Empleados',
      descripcion: 'InformaciÃ³n detallada del personal',
      icono: Users,
      color: 'blue',
      configuraciones: [
        {
          campo: 'campos',
          label: 'InformaciÃ³n a mostrar',
          tipo: 'multiselect',
          opciones: [
            { value: 'nombre', label: 'Nombre' },
            { value: 'apellido', label: 'Apellido' },
            { value: 'email', label: 'Email' },
            { value: 'cargo', label: 'Cargo' },
            { value: 'salario', label: 'Salario' },
            { value: 'fechaIngreso', label: 'Fecha de Ingreso' },
            { value: 'departamento', label: 'Departamento' },
            { value: 'estado', label: 'Estado' },
            { value: 'cedula', label: 'CÃ©dula' }
          ],
          default: ['nombre', 'apellido', 'cargo', 'departamento']
        },
        {
          campo: 'filtros.estado',
          label: 'Estado del empleado',
          tipo: 'select',
          opciones: [
            { value: 'todos', label: 'Todos' },
            { value: 'activo', label: 'Solo Activos' },
            { value: 'inactivo', label: 'Solo Inactivos' }
          ],
          default: 'activo'
        },
        {
          campo: 'filtros.departamentoId',
          label: 'Departamento',
          tipo: 'select-departamentos',
          default: null
        },
        {
          campo: 'ordenamiento.campo',
          label: 'Ordenar por',
          tipo: 'select',
