  function obtenerValorConfig(config, campo) {
    const campos = campo.split('.');
    let valor = config;
    for (const c of campos) {
      valor = valor?.[c];
    }
    return valor;
  }

  function actualizarConfiguracion(campo, valor) {
    const campos = campo.split('.');
    const nuevaConfig = { ...configuracion };
    
    let ref = nuevaConfig;
    for (let i = 0; i < campos.length - 1; i++) {
      if (!ref[campos[i]]) ref[campos[i]] = {};
      ref = ref[campos[i]];
    }
    
    ref[campos[campos.length - 1]] = valor;
    setConfiguracion(nuevaConfig);
  }

  function formatearNombreColumna(nombre) {
    return nombre
      .replace(/([A-Z])/g, ' $1')
      .replace(/_/g, ' ')
      .replace(/^./, str => str.toUpperCase())
      .trim();
  }

  function formatearValor(key, value) {
    if (value === null || value === undefined) return '-';
    
    // Fechas
    if (key.toLowerCase().includes('fecha') && value) {
      return new Date(value).toLocaleDateString('es-DO');
    }
    
    // Moneda
    if (key.toLowerCase().includes('salario') || 
        key.toLowerCase().includes('preaviso') ||
        key.toLowerCase().includes('cesantia') ||
        key.toLowerCase().includes('vacaciones') ||
        key.toLowerCase().includes('regalia') ||
        key.toLowerCase().includes('total') ||
        key.toLowerCase().includes('presupuesto')) {
      return `$${parseFloat(value).toLocaleString('es-DO', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    }
    
    // NÃºmeros
    if (typeof value === 'number') {
      return value.toLocaleString('es-DO');
    }
    
    return value;
  }

  function calcularTotalesSeverance(data) {
    const totales = {
      Preaviso: 0,
      Cesantia: 0,
      Vacaciones: 0,
      Regalia: 0,
      Total: 0
    };

    data.forEach(emp => {
      totales.Preaviso += emp.Preaviso || 0;
      totales.Cesantia += emp.Cesantia || 0;
      totales.Vacaciones += emp.Vacaciones || 0;
      totales.Regalia += emp.Regalia || 0;
      totales.Total += emp.Total || 0;
    });

    return (
      <>
        {Object.entries(totales).map(([key, value]) => (
          <div key={key}>
            <div className="text-xs text-blue-700 font-medium">{key}</div>
            <div className="text-lg font-bold text-blue-900">
              ${value.toLocaleString('es-DO', { minimumFractionDigits: 2 })}
            </div>
          </div>
        ))}
      </>
    );
  }
};

