import React, { useState } from 'react';
import { Plus, FileText, Eye, Building, User, Calendar } from 'lucide-react';

// Estados de ejemplo, puedes ajustarlos según tu proyecto
export const ESTADOS_SOLICITUD = {
  BORRADOR: { label: 'Borrador', color: '#9ca3af', bg: '#f3f4f6' },
  PENDIENTE_GERENTE_RRHH: { label: 'Pendiente Gerente RRHH', color: '#f59e0b', bg: '#fffbeb' },
  PENDIENTE_DIRECTOR_RRHH: { label: 'Pendiente Director RRHH', color: '#facc15', bg: '#fffbeb' },
  APROBADA: { label: 'Aprobada', color: '#16a34a', bg: '#dcfce7' },
  RECHAZADA: { label: 'Rechazada', color: '#dc2626', bg: '#fee2e2' },
};

const VistaMisSolicitudes = ({ solicitudes, onCrearSolicitud, onVerDetalles }) => {
  const [filtroEstado, setFiltroEstado] = useState('');

  const solicitudesFiltradas = solicitudes.filter(s => !filtroEstado || s.estado === filtroEstado);

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <div>
          <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            Mis Solicitudes de Vacantes
          </h2>
          <p style={{ color: '#6b7280', margin: '0.5rem 0 0 0' }}>
            Seguimiento de las solicitudes de vacantes que has creado
          </p>
        </div>

        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <select
            value={filtroEstado}
            onChange={e => setFiltroEstado(e.target.value)}
            style={{
              padding: '0.75rem',
              border: '1px solid #d1d5db',
              borderRadius: '0.5rem',
              fontSize: '0.875rem',
            }}
          >
            <option value="">Todos los estados</option>
            {Object.keys(ESTADOS_SOLICITUD).map(key => (
              <option key={key} value={key}>
                {ESTADOS_SOLICITUD[key].label}
              </option>
            ))}
          </select>

          <button
            onClick={onCrearSolicitud}
            style={{
              backgroundColor: '#4f46e5',
              color: 'white',
              padding: '0.75rem 1.5rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.875rem',
              fontWeight: '500',
            }}
          >
            <Plus size={16} />
            Nueva Solicitud
          </button>
        </div>
      </div>

      <div style={{ display: 'grid', gap: '1rem' }}>
        {solicitudesFiltradas.map(solicitud => (
          <TarjetaSolicitud
            key={solicitud.id}
            solicitud={solicitud}
            onVerDetalles={onVerDetalles}
          />
        ))}
      </div>

      {solicitudesFiltradas.length === 0 && (
        <div
          style={{
            textAlign: 'center',
            padding: '4rem 2rem',
            backgroundColor: '#f8fafc',
            borderRadius: '0.75rem',
            border: '2px dashed #d1d5db',
          }}
        >
          <FileText size={64} style={{ margin: '0 auto 1.5rem', color: '#9ca3af' }} />
          <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
            No tienes solicitudes
          </h3>
          <p style={{ color: '#6b7280', maxWidth: '400px', margin: '0 auto 1.5rem' }}>
            {filtroEstado
              ? `No tienes solicitudes con estado "${ESTADOS_SOLICITUD[filtroEstado]?.label}"`
              : 'Aún no has creado ninguna solicitud de vacante'}
          </p>
          <button
            onClick={onCrearSolicitud}
            style={{
              backgroundColor: '#4f46e5',
              color: 'white',
              padding: '0.75rem 1.5rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              display: 'inline-flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.875rem',
              fontWeight: '500',
            }}
          >
            <Plus size={16} />
            Crear Primera Solicitud
          </button>
        </div>
      )}
    </div>
  );
};

const TarjetaSolicitud = ({ solicitud, onVerDetalles }) => {
  const estadoConfig = ESTADOS_SOLICITUD[solicitud.estado] || { color: '#6b7280', bg: '#f3f4f6', label: 'Desconocido' };

  return (
    <div
      style={{
        border: '1px solid #e2e8f0',
        borderRadius: '0.75rem',
        padding: '1.5rem',
        backgroundColor: 'white',
      }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
        <div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.5rem' }}>
            <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: '#1f2937', margin: 0 }}>
              {solicitud.cargo}
            </h3>
            <span
              style={{
                backgroundColor: estadoConfig.bg,
                color: estadoConfig.color,
                padding: '0.25rem 0.75rem',
                borderRadius: '1rem',
                fontSize: '0.75rem',
                fontWeight: '500',
              }}
            >
              {estadoConfig.label}
            </span>
          </div>

          <div style={{ display: 'flex', gap: '1.5rem', fontSize: '0.875rem', color: '#6b7280' }}>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Building size={14} />
              {solicitud.departamento}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <User size={14} />
              {solicitud.solicitante}
            </span>
            <span style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
              <Calendar size={14} />
              {new Date(solicitud.fechaSolicitud).toLocaleDateString()}
            </span>
          </div>
        </div>

        <button
          onClick={() => onVerDetalles(solicitud)}
          style={{
            backgroundColor: '#f8fafc',
            border: '1px solid #e2e8f0',
            color: '#4f46e5',
            padding: '0.5rem 1rem',
            borderRadius: '0.5rem',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            fontSize: '0.875rem',
            fontWeight: '500',
          }}
        >
          <Eye size={16} />
          Ver Detalles
        </button>
      </div>
    </div>
  );
};

export default VistaMisSolicitudes;
