// src/components/modules/Reportes/ReportesModule.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BarChart, Bar, LineChart, Line, PieChart, Pie, Cell,
  XAxis, YAxis, Tooltip, Legend, ResponsiveContainer
} from "recharts";
import { useAuth } from "../../../context/AuthContext";

const API_URL = process.env.REACT_APP_API_URL || "http://192.168.0.239:5000/api";

const ReportesModule = () => {
  const { user, getStoredToken, logout } = useAuth();
  const [data, setData] = useState([]);
  const [reportesGuardados, setReportesGuardados] = useState([]);
  const [tipoReporte, setTipoReporte] = useState("empleados_por_departamento");
  const [grafico, setGrafico] = useState("tabla");
  const [filtros, setFiltros] = useState({ fechaInicio: "", fechaFin: "", departamento: "" });
  const [titulo, setTitulo] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // === Cliente Axios con token ===
  const api = axios.create({ baseURL: API_URL, timeout: 10000 });
  api.interceptors.request.use((config) => {
    const token = getStoredToken();
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
  }, (err) => Promise.reject(err));
  api.interceptors.response.use((res) => res, (err) => {
    if (err.response?.status === 401) {
      logout();
      alert("Sesión expirada, inicia sesión de nuevo.");
    }
    return Promise.reject(err);
  });

  // === Cargar reportes guardados del backend ===
  useEffect(() => {
    if (!user) return;
    const fetchGuardados = async () => {
      try {
        const res = await api.get("/reportes/mis");
        setReportesGuardados(res.data || []);
      } catch (err) {
        console.error("Error cargando reportes guardados:", err);
      }
    };
    fetchGuardados();
  }, [user]);

  // === Generar reporte (predefinido o con filtros) ===
  const fetchReporte = async () => {
    try {
      setLoading(true);
      setError(null);
      const res = await api.post(`/reportes/predefinidos/${tipoReporte}`, filtros);
      setData(res.data || []);
    } catch (err) {
      setError("Error al cargar el reporte");
    } finally {
      setLoading(false);
    }
  };

  // === Guardar reporte en backend ===
  const guardarReporte = async () => {
    if (!titulo.trim()) {
      alert("Ponle un nombre a tu reporte.");
      return;
    }
    try {
      const nuevo = { titulo, tipoReporte, filtros, grafico };
      const res = await api.post("/reportes/save", nuevo);
      setReportesGuardados([...reportesGuardados, res.data]);
      setTitulo("");
      alert("Reporte guardado en tus favoritos ✅");
    } catch (err) {
      console.error("Error guardando reporte:", err);
      alert("Error guardando el reporte.");
    }
  };

  // === Cargar reporte guardado ===
  const cargarReporte = (reporte) => {
    setTipoReporte(reporte.tipoReporte);
    setFiltros(reporte.filtros);
    setGrafico(reporte.grafico);
    fetchReporte();
  };

  // === Exportar reporte ===
  const exportar = async (formato) => {
    try {
      const res = await api.post(
        "/reportes/export",
        { formato, data },
        { responseType: "blob" }
      );
      const url = URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.download = `reporte.${formato}`;
      link.click();
    } catch {
      alert("Error exportando el reporte");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">📊 Reportes</h2>

      {/* Reportes rápidos */}
      <div className="flex gap-4 mb-4">
        <select
          value={tipoReporte}
          onChange={(e) => setTipoReporte(e.target.value)}
          className="border rounded p-2"
        >
          <option value="empleados_por_departamento">Empleados por departamento</option>
          <option value="vacantes_abiertas">Vacantes abiertas</option>
          <option value="contrataciones_recientes">Contrataciones recientes</option>
        </select>

        <input
          type="date"
          value={filtros.fechaInicio}
          onChange={(e) => setFiltros({ ...filtros, fechaInicio: e.target.value })}
          className="border rounded p-2"
        />
        <input
          type="date"
          value={filtros.fechaFin}
          onChange={(e) => setFiltros({ ...filtros, fechaFin: e.target.value })}
          className="border rounded p-2"
        />

        <input
          type="text"
          placeholder="Departamento"
          value={filtros.departamento}
          onChange={(e) => setFiltros({ ...filtros, departamento: e.target.value })}
          className="border rounded p-2"
        />

        <button
          onClick={fetchReporte}
          disabled={loading}
          className="px-4 py-2 bg-blue-600 text-white rounded"
        >
          {loading ? "Cargando..." : "Generar"}
        </button>
      </div>

      {/* Guardar */}
      <div className="flex gap-2 mb-6">
        <input
          type="text"
          placeholder="Nombre de mi reporte"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          className="border rounded p-2 flex-1"
        />
        <button
          onClick={guardarReporte}
          className="px-4 py-2 bg-green-600 text-white rounded"
        >
          Guardar
        </button>
      </div>

      {/* Reportes guardados */}
      {reportesGuardados.length > 0 && (
        <div className="mb-6">
          <h3 className="font-semibold mb-2">📂 Mis reportes guardados</h3>
          <div className="flex gap-2 flex-wrap">
            {reportesGuardados.map((rep) => (
              <button
                key={rep.id}
                onClick={() => cargarReporte(rep)}
                className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
              >
                {rep.titulo}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Exportar y gráficos */}
      {data.length > 0 && (
        <>
          <div className="flex justify-between items-center mb-4">
            <div>
              <button onClick={() => exportar("csv")} className="mr-2 px-3 py-1 bg-gray-600 text-white rounded">CSV</button>
              <button onClick={() => exportar("excel")} className="mr-2 px-3 py-1 bg-yellow-600 text-white rounded">Excel</button>
              <button onClick={() => exportar("pdf")} className="px-3 py-1 bg-red-600 text-white rounded">PDF</button>
            </div>
            <select value={grafico} onChange={(e) => setGrafico(e.target.value)} className="border rounded p-2">
              <option value="tabla">Tabla</option>
              <option value="barra">Barras</option>
              <option value="linea">Líneas</option>
              <option value="torta">Torta</option>
            </select>
          </div>

          {/* Tabla */}
          {grafico === "tabla" && (
            <table className="table-auto border-collapse border border-gray-400 w-full mb-6">
              <thead>
                <tr>
                  {Object.keys(data[0]).map((key) => (
                    <th key={key} className="border px-2 py-1">{key}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.map((row, i) => (
                  <tr key={i}>
                    {Object.values(row).map((val, j) => (
                      <td key={j} className="border px-2 py-1">{val}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          )}

          {/* Gráficos */}
          {grafico !== "tabla" && (
            <div style={{ width: "100%", height: 400 }}>
              <ResponsiveContainer>
                {grafico === "barra" && (
                  <BarChart data={data}>
                    <XAxis dataKey={Object.keys(data[0])[0]} />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey={Object.keys(data[0])[1]} fill="#3b82f6" />
                  </BarChart>
                )}
                {grafico === "linea" && (
                  <LineChart data={data}>
                    <XAxis dataKey={Object.keys(data[0])[0]} />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey={Object.keys(data[0])[1]} stroke="#10b981" />
                  </LineChart>
                )}
                {grafico === "torta" && (
                  <PieChart>
                    <Pie
                      data={data}
                      dataKey={Object.keys(data[0])[1]}
                      nameKey={Object.keys(data[0])[0]}
                      cx="50%"
                      cy="50%"
                      outerRadius={150}
                      fill="#6366f1"
                      label
                    >
                      {data.map((_, i) => (
                        <Cell key={i} fill={["#6366f1", "#f59e0b", "#10b981", "#ef4444"][i % 4]} />
                      ))}
                    </Pie>
                    <Tooltip />
                  </PieChart>
                )}
              </ResponsiveContainer>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ReportesModule;
