import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../../../context/AuthContext";
import { Building, Plus, Trash2, Edit, AlertCircle, CheckCircle, X, Save } from "lucide-react";

const API_URL = process.env.REACT_APP_API_URL || "http://192.168.0.239:5000/api";

const DepartamentosModule = () => {
  const { getStoredToken } = useAuth();
  const [departamentos, setDepartamentos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editingDepartamento, setEditingDepartamento] = useState(null);
  const [formData, setFormData] = useState({
    nombre: "",
    descripcion: ""
  });

  const fetchDepartamentos = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const token = getStoredToken();
      const res = await axios.get(`${API_URL}/departamentos`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      console.log('üìä Datos recibidos de departamentos:', res.data);
      
      // ‚úÖ VALIDAR FORMATO DE RESPUESTA
      if (res.data.departamentos && Array.isArray(res.data.departamentos)) {
        setDepartamentos(res.data.departamentos);
      } else if (Array.isArray(res.data)) {
        setDepartamentos(res.data);
      } else {
        console.error('Formato inesperado:', res.data);
        setDepartamentos([]);
        setError('Formato de datos inesperado');
      }
      
    } catch (err) {
      console.error('Error cargando departamentos:', err);
      setError(err.response?.data?.message || 'Error cargando departamentos');
      setDepartamentos([]);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (departamento = null) => {
    if (departamento) {
      setEditingDepartamento(departamento);
      setFormData({
        nombre: departamento.nombre || departamento.Nombre || departamento.NOMBRE || "",
        descripcion: departamento.descripcion || departamento.Descripcion || departamento.DESCRIPCION || ""
      });
    } else {
      setEditingDepartamento(null);
      setFormData({ nombre: "", descripcion: "" });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingDepartamento(null);
    setFormData({ nombre: "", descripcion: "" });
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.nombre.trim()) {
      setError('El nombre del departamento es requerido');
      return;
    }
    
    setLoading(true);
    setError(null);
    setMessage(null);
    
    try {
      const token = getStoredToken();
      
      if (editingDepartamento) {
        // EDITAR
        const id = editingDepartamento.id || editingDepartamento.DepartamentoID || editingDepartamento.DEPARTAMENTO_ID;
        await axios.put(
          `${API_URL}/departamentos/${id}`,
          {
            nombre: formData.nombre.trim(),
            descripcion: formData.descripcion.trim()
          },
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );
        setMessage('Departamento actualizado exitosamente');
      } else {
        // CREAR
        await axios.post(
          `${API_URL}/departamentos`,
          {
            nombre: formData.nombre.trim(),
            descripcion: formData.descripcion.trim()
          },
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );
        setMessage('Departamento creado exitosamente');
      }
      
      handleCloseModal();
      await fetchDepartamentos();
      
      // Limpiar mensaje despu√©s de 3 segundos
      setTimeout(() => setMessage(null), 3000);
      
    } catch (err) {
      console.error('Error guardando departamento:', err);
      setError(err.response?.data?.message || 'Error guardando departamento');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¬øEst√° seguro de eliminar este departamento?')) {
      return;
    }
    
    setLoading(true);
    setError(null);
    setMessage(null);
    
    try {
      const token = getStoredToken();
      await axios.delete(`${API_URL}/departamentos/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      setMessage('Departamento eliminado exitosamente');
      await fetchDepartamentos();
      
      // Limpiar mensaje despu√©s de 3 segundos
      setTimeout(() => setMessage(null), 3000);
      
    } catch (err) {
      console.error('Error eliminando departamento:', err);
      setError(err.response?.data?.message || 'Error eliminando departamento');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDepartamentos();
  }, []);

  // Limpiar mensajes despu√©s de 5 segundos
  useEffect(() => {
    if (error || message) {
      const timer = setTimeout(() => {
        setError(null);
        setMessage(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error, message]);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-sm mb-6 p-6">
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center gap-3 mb-2">
                <div className="p-2 bg-purple-100 rounded-lg">
                  <Building className="w-6 h-6 text-purple-600" />
                </div>
                <h2 className="text-2xl font-bold text-gray-900">Gesti√≥n de Departamentos</h2>
              </div>
              <p className="text-gray-600 text-sm">
                Administra los departamentos y estructura organizativa de la empresa
              </p>
            </div>
            <button
              onClick={() => handleOpenModal()}
              className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors flex items-center gap-2 font-medium"
            >
              <Plus className="w-5 h-5" />
              Nuevo Departamento
            </button>
          </div>
        </div>

        {/* Mensajes de estado */}
        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center gap-2 text-red-800">
            <AlertCircle className="w-5 h-5 flex-shrink-0" />
            <span>{error}</span>
          </div>
        )}

        {message && (
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center gap-2 text-green-800">
            <CheckCircle className="w-5 h-5 flex-shrink-0" />
            <span>{message}</span>
          </div>
        )}

        {/* Lista de departamentos */}
        <div className="bg-white rounded-lg shadow-sm">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">
              Departamentos Registrados ({Array.isArray(departamentos) ? departamentos.length : 0})
            </h3>
          </div>

          {loading && departamentos.length === 0 ? (
            <div className="p-8 text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600 mx-auto mb-4"></div>
              <p className="text-gray-600">Cargando departamentos...</p>
            </div>
          ) : Array.isArray(departamentos) && departamentos.length === 0 ? (
            <div className="p-8 text-center">
              <Building className="w-12 h-12 text-gray-300 mx-auto mb-4" />
              <p className="text-gray-600 font-medium">No hay departamentos registrados</p>
              <p className="text-gray-500 text-sm mt-1">Crea el primer departamento usando el bot√≥n de arriba</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-6">
              {Array.isArray(departamentos) && departamentos.map((dept) => {
                const id = dept.id || dept.DepartamentoID || dept.DEPARTAMENTO_ID;
                const nombre = dept.nombre || dept.Nombre || dept.NOMBRE || 'Sin nombre';
                const descripcion = dept.descripcion || dept.Descripcion || dept.DESCRIPCION || 'Sin descripci√≥n';
                
                return (
                  <div
                    key={id}
                    className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex items-center gap-2">
                        <div className="p-2 bg-purple-100 rounded-lg">
                          <Building className="w-5 h-5 text-purple-600" />
                        </div>
                        <h4 className="font-semibold text-gray-900">{nombre}</h4>
                      </div>
                    </div>
                    
                    <p className="text-gray-600 text-sm mb-4 line-clamp-2">
                      {descripcion}
                    </p>
                    
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleOpenModal(dept)}
                        disabled={loading}
                        className="flex-1 px-3 py-2 text-blue-600 bg-blue-50 hover:bg-blue-100 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 text-sm font-medium"
                      >
                        <Edit className="w-4 h-4" />
                        Editar
                      </button>
                      <button
                        onClick={() => handleDelete(id)}
                        disabled={loading}
                        className="flex-1 px-3 py-2 text-red-600 bg-red-50 hover:bg-red-100 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 text-sm font-medium"
                      >
                        <Trash2 className="w-4 h-4" />
                        Eliminar
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full">
            <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">
                {editingDepartamento ? 'Editar Departamento' : 'Nuevo Departamento'}
              </h3>
              <button
                onClick={handleCloseModal}
                className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <X className="w-5 h-5 text-gray-500" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="p-6 space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Nombre del Departamento *
                </label>
                <input
                  type="text"
                  value={formData.nombre}
                  onChange={(e) => setFormData({ ...formData, nombre: e.target.value })}
                  placeholder="Ej: Recursos Humanos"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Descripci√≥n
                </label>
                <textarea
                  value={formData.descripcion}
                  onChange={(e) => setFormData({ ...formData, descripcion: e.target.value })}
                  placeholder="Describe las funciones y responsabilidades del departamento"
                  rows="3"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none"
                />
              </div>

              <div className="flex gap-3 pt-4">
                <button
                  type="button"
                  onClick={handleCloseModal}
                  className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors font-medium"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="flex-1 px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 font-medium"
                >
                  <Save className="w-4 h-4" />
                  {loading ? 'Guardando...' : editingDepartamento ? 'Actualizar' : 'Crear'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DepartamentosModule;
