// Componentes base para modales
const ModalBase = ({ titulo, children, onClose }) => (
  <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
    <div style={{ backgroundColor: 'white', borderRadius: '0.75rem', padding: '2rem', maxWidth: '600px', width: '90%', maxHeight: '90vh', overflow: 'auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
        <h3 style={{ fontSize: '1.25rem', fontWeight: 'bold', margin: 0 }}>{titulo}</h3>
        <button onClick={onClose} style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '1.5rem' }}>
          <X size={24} />
        </button>
      </div>
      {children}
    </div>
  </div>
);

const Input = ({ label, value, onChange, type = 'text', required = false }) => (
  <div>
    <label style={labelStyle}>{label} {required && <span style={{ color: '#ef4444' }}>*</span>}</label>
    <input type={type} value={value} onChange={onChange} required={required} style={inputStyle} />
  </div>
);

const BotonesModal = ({ onSubmit, onCancel }) => (
  <div style={{ display: 'flex', gap: '0.5rem', marginTop: '1.5rem' }}>
    <button type="submit" style={{ flex: 1, backgroundColor: '#3b82f6', color: 'white', padding: '0.75rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', fontSize: '0.875rem', fontWeight: '500' }}>
      {onSubmit}
    </button>
    <button type="button" onClick={onCancel} style={{ flex: 1, backgroundColor: '#6b7280', color: 'white', padding: '0.75rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', fontSize: '0.875rem', fontWeight: '500' }}>
      Cancelar
    </button>
  </div>
);

const Notificacion = ({ type, message, onClose }) => {
  const isError = type === 'error';
  const bgColor = isError ? '#fee2e2' : '#d1fae5';
  const textColor = isError ? '#dc2626' : '#065f46';
  const Icon = isError ? AlertCircle : CheckCircle;

  return (
    <div style={{ position: 'fixed', top: '1rem', right: '1rem', backgroundColor: bgColor, borderRadius: '0.75rem', padding: '1rem', maxWidth: '400px', zIndex: 1000, boxShadow: '0 10px 25px rgba(0, 0, 0, 0.15)' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
        <Icon size={20} style={{ color: textColor }} />
        <div style={{ flex: 1, color: textColor, fontSize: '0.875rem' }}>{message}</div>
        <button onClick={onClose} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
          <X size={16} style={{ color: textColor }} />
        </button>
      </div>
    </div>
  );
};

// Estilos constantes
const labelStyle = { display: 'block', fontSize: '0.875rem', fontWeight: '500', marginBottom: '0.5rem' };
const inputStyle = { width: '100%', padding: '0.75rem', border: '1px solid #d1d5db', borderRadius: '0.5rem', fontSize: '0.875rem' };

export default VacantesModule;
