import React, { useState, useEffect } from 'react';
import { X, Check, XCircle, Eye } from 'lucide-react';
import * as accionesPersonalApi from '../../../api/accionesPersonalApi';

const DetalleAccion = ({ accionId, onClose, onSuccess }) => {
  const [accion, setAccion] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    cargarDetalle();
  }, [accionId]);

  const cargarDetalle = async () => {
    try {
      const resultado = await accionesPersonalApi.getAccionDetalle(accionId);
      if (resultado.success) {
        setAccion(resultado.accion);
      }
    } catch (error) {
      console.error('Error al cargar detalle:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 1000
      }}>
        <div style={{ background: 'white', padding: '2rem', borderRadius: '12px' }}>
          <p>Cargando...</p>
        </div>
      </div>
    );
  }

  if (!accion) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: '1rem'
    }}>
      <div style={{
        background: 'white',
        borderRadius: '12px',
        maxWidth: '700px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'auto',
        boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
      }}>
        {/* Header */}
        <div style={{
          padding: '1.5rem',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
            Detalle de Acción
          </h2>
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              padding: '0.5rem',
              color: '#6b7280'
            }}
          >
            <X size={24} />
          </button>
        </div>

        {/* Contenido */}
        <div style={{ padding: '1.5rem' }}>
          <div style={{ marginBottom: '1.5rem' }}>
            <h3 style={{ fontSize: '1.125rem', fontWeight: '600', marginBottom: '1rem' }}>
              {accion.NombreEmpleado}
            </h3>
            <p style={{ color: '#6b7280' }}>{accion.TipoAccion}</p>
            <p style={{ color: '#6b7280' }}>Estado: {accion.Estado}</p>
          </div>

          {accion.Estado === 'Pendiente' && (
            <div style={{ display: 'flex', gap: '1rem', marginTop: '1.5rem' }}>
              <button
                style={{
                  flex: 1,
                  padding: '0.75rem',
                  background: '#10b981',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '600'
                }}
                onClick={() => {
                  // Implementar aprobación
                  onSuccess();
                  onClose();
                }}
              >
                <Check size={18} style={{ display: 'inline', marginRight: '0.5rem' }} />
                Aprobar
              </button>
              <button
                style={{
                  flex: 1,
                  padding: '0.75rem',
                  background: '#ef4444',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '600'
                }}
                onClick={() => {
                  // Implementar rechazo
                  onSuccess();
                  onClose();
                }}
              >
                <XCircle size={18} style={{ display: 'inline', marginRight: '0.5rem' }} />
                Rechazar
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DetalleAccion;
